"use client";

import { motion } from "framer-motion";
import { useState, useEffect } from "react";
import { useSearchParams } from "next/navigation";
import {
  FiMail,
  FiUser,
  FiMessageSquare,
  FiDollarSign,
  FiCalendar,
  FiSend,
} from "react-icons/fi";

const serviceOptions = [
  { id: "full-stack", label: "Full-Stack Development" },
  { id: "performance", label: "Performance Optimization" },
  { id: "consulting", label: "Technical Consulting" },
  { id: "custom", label: "Custom Project" },
];

const budgetRanges = [
  "Under $2,500",
  "$2,500 - $5,000",
  "$5,000 - $10,000",
  "$10,000 - $25,000",
  "$25,000+",
];

const timelineOptions = [
  "ASAP (1-2 weeks)",
  "1 month",
  "2-3 months",
  "3+ months",
  "Flexible",
];

export default function ContactPage() {
  const searchParams = useSearchParams();
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    service: "",
    budget: "",
    timeline: "",
    message: "",
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState(null);

  useEffect(() => {
    const serviceParam = searchParams.get("service");
    const messageParam = searchParams.get("message");

    setFormData((prev) => ({
      ...prev,
      service: serviceParam || prev.service,
      message: messageParam || prev.message,
    }));
  }, [searchParams]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSubmitStatus(null);

    try {
      // Simulate API call - replace with actual endpoint
      await new Promise((resolve) => setTimeout(resolve, 1500));

      // TODO: Integrate with backend API
      console.log("Form submitted:", formData);

      setSubmitStatus("success");
      setFormData({
        name: "",
        email: "",
        service: "",
        budget: "",
        timeline: "",
        message: "",
      });
    } catch (error) {
      setSubmitStatus("error");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="container py-5 mt-4">
      <div className="row justify-content-center">
        <div className="col-lg-8">
          {/* Header */}
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="text-center mb-5"
          >
            <span className="badge bg-primary bg-opacity-10 text-primary border border-primary border-opacity-25 px-4 py-2 rounded-pill mb-3">
              Get In Touch
            </span>
            <h1 className="display-4 fw-bold mb-4">
              Let's Discuss Your <span className="text-primary">Project</span>
            </h1>
            <p className="lead text-muted">
              Fill out the form below and I'll get back to you within 24 hours.
            </p>
          </motion.div>

          {/* Contact Form */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2 }}
            className="glass-card p-4 p-md-5"
          >
            <form onSubmit={handleSubmit}>
              <div className="row g-4">
                {/* Name */}
                <div className="col-md-6">
                  <label className="form-label d-flex align-items-center gap-2">
                    <FiUser size={16} />
                    Full Name *
                  </label>
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    className="form-control form-control-lg bg-transparent"
                    placeholder="John Doe"
                    required
                  />
                </div>

                {/* Email */}
                <div className="col-md-6">
                  <label className="form-label d-flex align-items-center gap-2">
                    <FiMail size={16} />
                    Email Address *
                  </label>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    className="form-control form-control-lg bg-transparent"
                    placeholder="john@example.com"
                    required
                  />
                </div>

                {/* Service Type */}
                <div className="col-12">
                  <label className="form-label d-flex align-items-center gap-2">
                    <FiMessageSquare size={16} />
                    Service Interested In *
                  </label>
                  <select
                    name="service"
                    value={formData.service}
                    onChange={handleChange}
                    className="form-select form-select-lg bg-transparent"
                    required
                  >
                    <option value="" className="text-dark">
                      Select a service...
                    </option>
                    {serviceOptions.map((option) => (
                      <option
                        key={option.id}
                        value={option.id}
                        className="text-dark"
                      >
                        {option.label}
                      </option>
                    ))}
                  </select>
                </div>

                {/* Budget */}
                <div className="col-md-6">
                  <label className="form-label d-flex align-items-center gap-2">
                    <FiDollarSign size={16} />
                    Project Budget *
                  </label>
                  <select
                    name="budget"
                    value={formData.budget}
                    onChange={handleChange}
                    className="form-select form-select-lg bg-transparent"
                    required
                  >
                    <option value="" className="text-dark">
                      Select budget range...
                    </option>
                    {budgetRanges.map((range) => (
                      <option key={range} value={range} className="text-dark">
                        {range}
                      </option>
                    ))}
                  </select>
                </div>

                {/* Timeline */}
                <div className="col-md-6">
                  <label className="form-label d-flex align-items-center gap-2">
                    <FiCalendar size={16} />
                    Desired Timeline *
                  </label>
                  <select
                    name="timeline"
                    value={formData.timeline}
                    onChange={handleChange}
                    className="form-select form-select-lg bg-transparent"
                    required
                  >
                    <option value="" className="text-dark">
                      Select timeline...
                    </option>
                    {timelineOptions.map((option) => (
                      <option key={option} value={option} className="text-dark">
                        {option}
                      </option>
                    ))}
                  </select>
                </div>

                {/* Message */}
                <div className="col-12">
                  <label className="form-label d-flex align-items-center gap-2">
                    <FiMessageSquare size={16} />
                    Project Details *
                  </label>
                  <textarea
                    name="message"
                    value={formData.message}
                    onChange={handleChange}
                    className="form-control bg-transparent"
                    rows="6"
                    placeholder="Tell me about your project, goals, and any specific requirements..."
                    required
                  />
                </div>

                {/* Submit Button */}
                <div className="col-12">
                  <button
                    type="submit"
                    disabled={isSubmitting}
                    className="btn btn-primary btn-lg w-100 d-flex align-items-center justify-content-center gap-2"
                  >
                    {isSubmitting ? (
                      <>
                        <span
                          className="spinner-border spinner-border-sm"
                          role="status"
                          aria-hidden="true"
                        />
                        Sending...
                      </>
                    ) : (
                      <>
                        <FiSend />
                        Send Inquiry
                      </>
                    )}
                  </button>
                </div>

                {/* Status Messages */}
                {submitStatus === "success" && (
                  <div className="col-12">
                    <div className="alert alert-success d-flex align-items-center gap-2 mb-0">
                      <FiMessageSquare />
                      Thank you! I'll review your inquiry and get back to you
                      within 24 hours.
                    </div>
                  </div>
                )}

                {submitStatus === "error" && (
                  <div className="col-12">
                    <div className="alert alert-danger mb-0">
                      Something went wrong. Please try again or email me
                      directly.
                    </div>
                  </div>
                )}
              </div>
            </form>
          </motion.div>

          {/* Additional Contact Info */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.4 }}
            className="text-center mt-5"
          >
            <p className="text-muted">
              Prefer email? Reach me at{" "}
              <a
                href="mailto:hello@example.com"
                className="text-primary text-decoration-none"
              >
                hello@example.com
              </a>
            </p>
          </motion.div>
        </div>
      </div>
    </div>
  );
}

"use client";

import { motion } from "framer-motion";
import Link from "next/link";
import {
  FiCheck,
  FiArrowRight,
  FiCode,
  FiZap,
  FiTrendingUp,
} from "react-icons/fi";
import styles from "../../styles/ServicesProcess.module.scss";
import ServicesSlider from "@/components/services/ServicesSlider";

const services = [
  {
    id: "full-stack",
    title: "Full-Stack Development",
    icon: <FiCode size={32} />,
    description:
      "End-to-end web application development with modern tech stacks",
    features: [
      "React/Next.js frontend",
      "Node.js/Express backend",
      "MongoDB/PostgreSQL databases",
      "RESTful & GraphQL APIs",
      "Authentication & Authorization",
      "Deployment & DevOps",
    ],
    pricing: "Starting at $5,000",
    timeline: "4-8 weeks",
    popular: true,
  },
  {
    id: "performance",
    title: "Performance Optimization",
    icon: <FiZap size={32} />,
    description:
      "Speed up your existing applications and improve user experience",
    features: [
      "Lighthouse score optimization",
      "Code splitting & lazy loading",
      "Database query optimization",
      "CDN & caching strategies",
      "Bundle size reduction",
      "Core Web Vitals improvement",
    ],
    pricing: "Starting at $2,500",
    timeline: "2-4 weeks",
    popular: false,
  },
  {
    id: "consulting",
    title: "Technical Consulting",
    icon: <FiTrendingUp size={32} />,
    description:
      "Expert guidance on architecture, tech stack, and best practices",
    features: [
      "Architecture review",
      "Tech stack recommendations",
      "Code review & refactoring",
      "Team mentoring",
      "Performance audits",
      "Security assessments",
    ],
    pricing: "Starting at $150/hour",
    timeline: "Flexible",
    popular: false,
  },
];

export default function ServicesPage() {
  return (
    <div className="container py-5 mt-4">
      {/* Hero Section */}
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="text-center mb-5 py-5"
      >
        <span className="badge bg-primary bg-opacity-10 text-primary border border-primary border-opacity-25 px-4 py-2 rounded-pill mb-3">
          Professional Services
        </span>
        <h1 className={`display-3 fw-bold mb-4 ${styles.heroTitle}`}>
          Let's Build Something <span className={styles.heroSpan}>Amazing</span>
        </h1>
        <p
          className={`lead text-muted mx-auto ${styles.heroDesc}`}
          style={{ maxWidth: "700px" }}
        >
          Transform your ideas into high-performance, scalable applications.
          Choose a service package that fits your needs.
        </p>
      </motion.div>

      {/* Services Grid - Desktop Only */}
      <div className="row g-4 mb-5 d-none d-md-flex">
        {services.map((service, index) => (
          <motion.div
            key={service.id}
            className="col-lg-4 col-md-6"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
          >
            <div
              className={`glass-card h-100 p-4 position-relative ${service.popular ? "border-primary border-2" : ""}`}
              style={{
                background: service.popular
                  ? "linear-gradient(135deg, rgba(124, 58, 237, 0.05) 0%, rgba(168, 85, 247, 0.02) 100%)"
                  : "rgba(255, 255, 255, 0.02)",
              }}
            >
              {service.popular && (
                <div className="position-absolute top-0 end-0 m-3">
                  <span className="badge bg-primary px-3 py-2">
                    Most Popular
                  </span>
                </div>
              )}

              <div className="mb-4">
                <div
                  className="d-inline-flex align-items-center justify-content-center rounded-3 p-3 mb-3"
                  style={{ background: "rgba(124, 58, 237, 0.1)" }}
                >
                  <div className="text-primary">{service.icon}</div>
                </div>
                <h3 className="h4 fw-bold mb-2">{service.title}</h3>
                <p className="text-muted small mb-3">{service.description}</p>
              </div>

              <div className="mb-4">
                <div className="d-flex justify-content-between align-items-center mb-2">
                  <span className="text-muted small">Investment</span>
                  <span className="fw-bold text-primary">
                    {service.pricing}
                  </span>
                </div>
                <div className="d-flex justify-content-between align-items-center">
                  <span className="text-muted small">Timeline</span>
                  <span className="fw-medium">{service.timeline}</span>
                </div>
              </div>

              <div className="border-top border-white border-opacity-10 pt-4 mb-4">
                <h5 className="h6 fw-bold mb-3">What's Included:</h5>
                <ul className="list-unstyled">
                  {service.features.map((feature, idx) => (
                    <li
                      key={idx}
                      className="d-flex align-items-start gap-2 mb-2"
                    >
                      <FiCheck
                        className="text-success mt-1 flex-shrink-0"
                        size={16}
                      />
                      <span className="small">{feature}</span>
                    </li>
                  ))}
                </ul>
              </div>

              <Link
                href={`/contact?service=${service.id}`}
                className={`btn ${service.popular ? "btn-primary" : "btn-outline-light"} w-100 d-flex align-items-center justify-content-center gap-2`}
              >
                Get Started <FiArrowRight />
              </Link>
            </div>
          </motion.div>
        ))}
      </div>

      {/* Services Slider - Mobile Only */}
      <ServicesSlider services={services} />

      {/* Custom Projects CTA */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.4 }}
        className="glass-card p-5 text-center"
        style={{
          background:
            "linear-gradient(135deg, rgba(124, 58, 237, 0.1) 0%, rgba(168, 85, 247, 0.05) 100%)",
        }}
      >
        <h2 className="h3 fw-bold mb-3">Need Something Custom?</h2>
        <p
          className="text-muted mb-4"
          style={{ maxWidth: "600px", margin: "0 auto" }}
        >
          Every project is unique. Let's discuss your specific requirements and
          create a tailored solution that perfectly fits your needs.
        </p>
        <Link
          href="/contact"
          className="btn btn-primary btn-lg px-5 rounded-pill"
        >
          Schedule a Consultation
        </Link>
      </motion.div>

      <div className={styles.processContainer}>
        <h2 className="h3 fw-bold text-center mb-5">How It Works</h2>

        <div className={`position-relative ${styles.mobileScrollWrapper}`}>
          {/* Progress Line */}
          <div className={styles.progressLine}>
            <div className={styles.progressGradient} />
          </div>

          <div
            className={`row g-4 position-relative ${styles.processRow}`}
            style={{ zIndex: 1 }}
          >
            {[
              {
                step: "01",
                title: "Discovery",
                desc: "We'll define your core objectives, target audience, and functional requirements to ensure a perfect project fit.",
                icon: "ðŸ”",
              },
              {
                step: "02",
                title: "Proposal",
                desc: "A comprehensive roadmap covering feature scope, selected tech stack, and a clear, transparent project timeline.",
                icon: "ðŸ“",
              },
              {
                step: "03",
                title: "Development",
                desc: "Agile, transparent coding with continuous integration and regular milestones to keep your project on track.",
                icon: "ðŸ’»",
              },
              {
                step: "04",
                title: "Launch",
                desc: "Ensuring a flawless deployment followed by ongoing support and performance monitoring for long-term growth.",
                icon: "ðŸš€",
              },
            ].map((item, idx) => (
              <motion.div
                key={idx}
                className={`col-lg-3 col-md-6 ${styles.stepColumn}`}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.2 + idx * 0.1 }}
              >
                <div className={styles.stepCard}>
                  <div className={styles.stepCircle}>
                    <div className={styles.stepContent}>
                      <span className={styles.stepIcon}>{item.icon}</span>
                      <span className={styles.stepLabel}>STEP {item.step}</span>
                    </div>
                  </div>

                  <h4 className={styles.stepTitle}>{item.title}</h4>
                  <p className={styles.stepDesc}>{item.desc}</p>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import { useRouter } from "next/navigation";
import { motion } from "framer-motion";
import Link from "next/link";
import {
  FiTrendingUp,
  FiClock,
  FiAward,
  FiCode,
  FiDatabase,
  FiUsers,
  FiBriefcase,
} from "react-icons/fi";
import { useSelector } from "react-redux";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from "recharts";

const roleIcons = {
  frontend: <FiCode />,
  backend: <FiDatabase />,
  hr: <FiUsers />,
  custom: <FiBriefcase />,
};

export default function DashboardPage() {
  const router = useRouter();
  const { user } = useSelector((state) => state.auth);
  const [sessions, setSessions] = useState([]);
  const [analytics, setAnalytics] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (!user) {
      router.push("/login");
      return;
    }

    fetchData();
  }, [user]);

  const fetchData = async () => {
    try {
      const [sessionsRes, analyticsRes] = await Promise.all([
        fetch(
          `${process.env.NEXT_PUBLIC_API_URL}/api/interview/sessions?limit=10`,
          {
            headers: { Authorization: `Bearer ${user.token}` },
          },
        ),
        fetch(`${process.env.NEXT_PUBLIC_API_URL}/api/interview/analytics`, {
          headers: { Authorization: `Bearer ${user.token}` },
        }),
      ]);

      const sessionsData = await sessionsRes.json();
      const analyticsData = await analyticsRes.json();

      if (sessionsData.success) setSessions(sessionsData.data);
      if (analyticsData.success) setAnalytics(analyticsData.data);
    } catch (error) {
      console.error("Fetch error:", error);
    } finally {
      setIsLoading(false);
    }
  };

  if (isLoading) {
    return (
      <div className="container py-5 mt-4 text-center">
        <div className="spinner-border text-primary" />
        <p className="mt-3 text-muted">Loading dashboard...</p>
      </div>
    );
  }

  return (
    <div className="container py-5 mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1 className="h3 fw-bold">Interview Dashboard</h1>
        <Link
          href="/tools/interview-simulator/start"
          className="btn btn-primary"
        >
          New Interview
        </Link>
      </div>

      {/* Stats Cards */}
      <div className="row g-4 mb-5">
        <div className="col-md-4">
          <div className="glass-card p-4">
            <div className="d-flex align-items-center gap-3">
              <div
                className="d-inline-flex align-items-center justify-content-center rounded-3 p-3"
                style={{ background: "rgba(124, 58, 237, 0.1)" }}
              >
                <FiTrendingUp size={24} className="text-primary" />
              </div>
              <div>
                <div className="h4 fw-bold mb-0">
                  {analytics?.averageScore?.toFixed(1) || "0.0"}/10
                </div>
                <div className="small text-muted">Average Score</div>
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-4">
          <div className="glass-card p-4">
            <div className="d-flex align-items-center gap-3">
              <div
                className="d-inline-flex align-items-center justify-content-center rounded-3 p-3"
                style={{ background: "rgba(16, 185, 129, 0.1)" }}
              >
                <FiAward size={24} className="text-success" />
              </div>
              <div>
                <div className="h4 fw-bold mb-0">
                  {analytics?.totalSessions || 0}
                </div>
                <div className="small text-muted">Total Sessions</div>
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-4">
          <div className="glass-card p-4">
            <div className="d-flex align-items-center gap-3">
              <div
                className="d-inline-flex align-items-center justify-content-center rounded-3 p-3"
                style={{ background: "rgba(59, 130, 246, 0.1)" }}
              >
                <FiClock size={24} className="text-info" />
              </div>
              <div>
                <div className="h4 fw-bold mb-0">
                  {user?.subscription === "free" ? "3" : "âˆž"}
                </div>
                <div className="small text-muted">Sessions/Month</div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Score Trend */}
      {analytics?.recentScores && analytics.recentScores.length > 0 && (
        <div className="glass-card p-4 mb-5">
          <h2 className="h5 fw-bold mb-4">Score Trend</h2>
          <ResponsiveContainer width="100%" height={250}>
            <LineChart data={analytics.recentScores.reverse()}>
              <CartesianGrid
                strokeDasharray="3 3"
                stroke="rgba(255,255,255,0.1)"
              />
              <XAxis
                dataKey="date"
                tickFormatter={(date) => new Date(date).toLocaleDateString()}
                stroke="rgba(255,255,255,0.5)"
              />
              <YAxis domain={[0, 10]} stroke="rgba(255,255,255,0.5)" />
              <Tooltip
                contentStyle={{
                  background: "rgba(0,0,0,0.8)",
                  border: "1px solid rgba(255,255,255,0.1)",
                  borderRadius: "8px",
                }}
              />
              <Line
                type="monotone"
                dataKey="score"
                stroke="#7c3aed"
                strokeWidth={2}
                dot={{ fill: "#7c3aed" }}
              />
            </LineChart>
          </ResponsiveContainer>
        </div>
      )}

      {/* Recent Sessions */}
      <div className="glass-card p-4">
        <h2 className="h5 fw-bold mb-4">Recent Sessions</h2>
        {sessions.length === 0 ? (
          <div className="text-center py-5">
            <p className="text-muted">
              No sessions yet. Start your first interview!
            </p>
            <Link
              href="/tools/interview-simulator/start"
              className="btn btn-primary mt-3"
            >
              Start Interview
            </Link>
          </div>
        ) : (
          <div className="table-responsive">
            <table className="table table-hover">
              <thead>
                <tr>
                  <th>Role</th>
                  <th>Difficulty</th>
                  <th>Score</th>
                  <th>Duration</th>
                  <th>Date</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {sessions.map((session) => (
                  <tr key={session._id}>
                    <td>
                      <div className="d-flex align-items-center gap-2">
                        {roleIcons[session.role] || <FiBriefcase />}
                        <span className="text-capitalize">
                          {session.role === "custom" &&
                          session.customData?.title
                            ? session.customData.title
                            : session.role}
                        </span>
                      </div>
                    </td>
                    <td className="text-capitalize">{session.difficulty}</td>
                    <td>
                      {session.overallScore ? (
                        <span className="badge bg-primary">
                          {session.overallScore.toFixed(1)}/10
                        </span>
                      ) : (
                        "-"
                      )}
                    </td>
                    <td>
                      {session.duration
                        ? `${Math.floor(session.duration / 60)}m ${session.duration % 60}s`
                        : "-"}
                    </td>
                    <td>{new Date(session.createdAt).toLocaleDateString()}</td>
                    <td>
                      <span
                        className={`badge ${
                          session.status === "completed"
                            ? "bg-success"
                            : session.status === "active"
                              ? "bg-warning text-dark"
                              : "bg-secondary"
                        }`}
                      >
                        {session.status}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

"use client";

import { useState, useEffect, useRef } from "react";
import { useRouter } from "next/navigation";
import { motion, AnimatePresence } from "framer-motion";
import { FiSend, FiClock, FiX, FiCheck, FiTrendingUp } from "react-icons/fi";
import { useSelector } from "react-redux";
import { Prism as SyntaxHighlighter } from "react-syntax-highlighter";
import { vscDarkPlus } from "react-syntax-highlighter/dist/esm/styles/prism";

export default function InterviewSessionPage({ params }) {
  const router = useRouter();
  const { user } = useSelector((state) => state.auth);
  const [session, setSession] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [isSending, setIsSending] = useState(false);
  const [isEnding, setIsEnding] = useState(false);
  const [showFeedback, setShowFeedback] = useState(true);
  const [elapsedTime, setElapsedTime] = useState(0);
  const messagesEndRef = useRef(null);
  const timerRef = useRef(null);

  const sessionId = params.id;

  // Fetch session and messages
  useEffect(() => {
    if (!user) {
      router.push("/login");
      return;
    }

    fetchSession();
    fetchMessages();

    // Start timer
    timerRef.current = setInterval(() => {
      setElapsedTime((prev) => prev + 1);
    }, 1000);

    return () => {
      if (timerRef.current) clearInterval(timerRef.current);
    };
  }, [sessionId, user]);

  // Auto-scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const fetchSession = async () => {
    try {
      const response = await fetch(
        `${process.env.NEXT_PUBLIC_API_URL}/api/interview/sessions/${sessionId}`,
        {
          headers: { Authorization: `Bearer ${user.token}` },
        },
      );
      const data = await response.json();
      if (data.success) {
        setSession(data.data);
      }
    } catch (error) {
      console.error("Fetch session error:", error);
    }
  };

  const fetchMessages = async () => {
    try {
      const response = await fetch(
        `${process.env.NEXT_PUBLIC_API_URL}/api/interview/sessions/${sessionId}/messages`,
        {
          headers: { Authorization: `Bearer ${user.token}` },
        },
      );
      const data = await response.json();
      if (data.success) {
        setMessages(data.data);
      }
    } catch (error) {
      console.error("Fetch messages error:", error);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || isSending) return;

    setIsSending(true);
    const messageContent = inputMessage;
    setInputMessage("");

    try {
      const response = await fetch(
        `${process.env.NEXT_PUBLIC_API_URL}/api/interview/sessions/${sessionId}/messages`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${user.token}`,
          },
          body: JSON.stringify({ content: messageContent }),
        },
      );

      const data = await response.json();
      if (data.success) {
        setMessages((prev) => [
          ...prev,
          data.data.candidateMessage,
          data.data.interviewerMessage,
        ]);
      }
    } catch (error) {
      console.error("Send message error:", error);
    } finally {
      setIsSending(false);
    }
  };

  const handleEndInterview = async () => {
    if (!confirm("Are you sure you want to end this interview?")) return;

    setIsEnding(true);
    try {
      const response = await fetch(
        `${process.env.NEXT_PUBLIC_API_URL}/api/interview/sessions/${sessionId}/end`,
        {
          method: "PATCH",
          headers: { Authorization: `Bearer ${user.token}` },
        },
      );

      const data = await response.json();
      if (data.success) {
        router.push(`/tools/interview-simulator/summary/${sessionId}`);
      }
    } catch (error) {
      console.error("End interview error:", error);
      setIsEnding(false);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, "0")}`;
  };

  if (!session) {
    return (
      <div className="container py-5 mt-4 text-center">
        <div className="spinner-border text-primary" />
        <p className="mt-3 text-muted">Loading interview session...</p>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4" style={{ maxWidth: "1400px" }}>
      {/* Header */}
      <div className="glass-card p-3 mb-3 d-flex justify-content-between align-items-center">
        <div>
          <h1 className="h5 mb-1 fw-bold text-capitalize">
            {session.role === "custom" && session.customData?.title
              ? session.customData.title
              : session.role}{" "}
            Interview
          </h1>
          <span className="badge bg-primary bg-opacity-10 text-primary text-capitalize">
            {session.difficulty} Level
          </span>
        </div>
        <div className="d-flex align-items-center gap-3">
          <div className="d-flex align-items-center gap-2">
            <FiClock />
            <span className="fw-bold">{formatTime(elapsedTime)}</span>
          </div>
          <button
            onClick={handleEndInterview}
            disabled={isEnding}
            className="btn btn-outline-danger btn-sm"
          >
            {isEnding ? "Ending..." : "End Interview"}
          </button>
        </div>
      </div>

      <div className="row g-3">
        {/* Chat Area */}
        <div className={showFeedback ? "col-lg-8" : "col-12"}>
          <div
            className="glass-card p-4"
            style={{ height: "calc(100vh - 250px)" }}
          >
            {/* Messages */}
            <div
              className="overflow-auto mb-3"
              style={{ height: "calc(100% - 80px)" }}
            >
              <AnimatePresence>
                {messages.map((message, idx) => (
                  <motion.div
                    key={message._id || idx}
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className={`mb-3 d-flex ${
                      message.role === "candidate" ? "justify-content-end" : ""
                    }`}
                  >
                    <div
                      className={`p-3 rounded-3 ${
                        message.role === "interviewer"
                          ? "glass-card"
                          : "bg-primary text-white"
                      }`}
                      style={{ maxWidth: "75%" }}
                    >
                      <div className="small opacity-75 mb-1">
                        {message.role === "interviewer"
                          ? "AI Interviewer"
                          : "You"}
                      </div>
                      <div>{message.content}</div>
                      {message.feedback && (
                        <div className="mt-2 pt-2 border-top border-white border-opacity-25">
                          <div className="small">
                            <FiCheck className="me-1" />
                            Score: {message.feedback.score}/10
                          </div>
                        </div>
                      )}
                    </div>
                  </motion.div>
                ))}
              </AnimatePresence>
              <div ref={messagesEndRef} />
            </div>

            {/* Input */}
            <form onSubmit={handleSendMessage}>
              <div className="input-group">
                <input
                  type="text"
                  value={inputMessage}
                  onChange={(e) => setInputMessage(e.target.value)}
                  placeholder="Type your answer..."
                  className="form-control"
                  disabled={isSending}
                />
                <button
                  type="submit"
                  disabled={isSending || !inputMessage.trim()}
                  className="btn btn-primary"
                >
                  {isSending ? (
                    <span className="spinner-border spinner-border-sm" />
                  ) : (
                    <FiSend />
                  )}
                </button>
              </div>
            </form>
          </div>
        </div>

        {/* Feedback Panel */}
        {showFeedback && (
          <div className="col-lg-4">
            <div
              className="glass-card p-4"
              style={{ height: "calc(100vh - 250px)" }}
            >
              <div className="d-flex justify-content-between align-items-center mb-3">
                <h3 className="h6 fw-bold mb-0">Performance Insights</h3>
                <button
                  onClick={() => setShowFeedback(false)}
                  className="btn btn-sm btn-outline-light"
                >
                  <FiX />
                </button>
              </div>

              <div className="mb-4">
                <div className="d-flex justify-content-between mb-2">
                  <span className="small">Questions Answered</span>
                  <span className="fw-bold">
                    {Math.floor(messages.length / 2)}
                  </span>
                </div>
                <div className="progress" style={{ height: "8px" }}>
                  <div
                    className="progress-bar bg-primary"
                    style={{
                      width: `${Math.min((messages.length / 20) * 100, 100)}%`,
                    }}
                  />
                </div>
              </div>

              <div className="small text-muted">
                <FiTrendingUp className="me-2" />
                Keep answering questions to get detailed feedback and
                performance metrics!
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

// ... imports
import {
  FiCode,
  FiDatabase,
  FiUsers,
  FiArrowRight,
  FiPlusCircle,
  FiX,
} from "react-icons/fi";

// ... existing roles

export default function StartInterviewPage() {
  const router = useRouter();
  const { user } = useSelector((state) => state.auth);
  const [selectedRole, setSelectedRole] = useState(null);
  const [selectedDifficulty, setSelectedDifficulty] = useState("mid");
  const [isCreating, setIsCreating] = useState(false);
  const [error, setError] = useState(null);

  // Custom Interview State
  const [showCustomModal, setShowCustomModal] = useState(false);
  const [customRoleData, setCustomRoleData] = useState({
    title: "",
    jobDescription: "",
    focusAreas: "",
  });

  const handleStartInterview = async () => {
    if (!selectedRole && !showCustomModal) {
      setError("Please select an interview type");
      return;
    }

    if (!user) {
      router.push("/login?redirect=/tools/interview-simulator/start");
      return;
    }

    // specific logic for custom role validation
    if (selectedRole === "custom") {
      if (!customRoleData.title || !customRoleData.jobDescription) {
        setError("Please fill in the Job Title and Description.");
        return;
      }
    }

    setIsCreating(true);
    setError(null);

    try {
      const payload = {
        role: selectedRole,
        difficulty: selectedDifficulty,
        ...(selectedRole === "custom" && { customData: customRoleData }),
      };

      const response = await fetch(
        `${process.env.NEXT_PUBLIC_API_URL}/api/interview/sessions`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${user.token}`,
          },
          body: JSON.stringify(payload),
        },
      );

      const data = await response.json();

      if (!response.ok) {
        if (response.status === 403) {
          setError(data.error);
          setTimeout(() => router.push("/pricing"), 2000);
          return;
        }
        throw new Error(data.error || "Failed to create session");
      }

      router.push(
        `/tools/interview-simulator/session/${data.data.session._id}`,
      );
    } catch (err) {
      setError(err.message);
      setIsCreating(false);
    }
  };

  return (
    <div className="container py-5 mt-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="text-center mb-5"
      >
        <h1 className="display-4 fw-bold mb-3">Start Your Interview</h1>
        <p className="lead text-muted">
          Choose your role or create a custom scenario
        </p>
      </motion.div>

      {/* Role Selection */}
      <div className="mb-5">
        <h2 className="h4 fw-bold mb-4">Select Interview Type</h2>
        <div className="row g-4">
          {interviewRoles.map((role, idx) => (
            <motion.div
              key={role.id}
              className="col-lg-3 col-md-6"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: idx * 0.1 }}
            >
              <div
                onClick={() => {
                  setSelectedRole(role.id);
                  setShowCustomModal(false);
                }}
                className={`glass-card h-100 p-4 cursor-pointer ${
                  selectedRole === role.id ? "border-primary border-2" : ""
                }`}
                style={{
                  cursor: "pointer",
                  background:
                    selectedRole === role.id
                      ? role.gradient
                      : "rgba(255, 255, 255, 0.02)",
                  transition: "all 0.3s ease",
                }}
              >
                <div
                  className="d-inline-flex align-items-center justify-content-center rounded-3 p-3 mb-3"
                  style={{ background: role.color }}
                >
                  <div className="text-primary">{role.icon}</div>
                </div>
                <h3 className="h6 fw-bold mb-2">{role.title}</h3>
                <div className="d-flex flex-wrap gap-1">
                  {role.topics.slice(0, 2).map((topic, i) => (
                    <span
                      key={i}
                      className="badge bg-primary bg-opacity-10 text-primary small"
                      style={{ fontSize: "0.7rem" }}
                    >
                      {topic}
                    </span>
                  ))}
                </div>
              </div>
            </motion.div>
          ))}

          {/* Custom Role Card */}
          <motion.div
            key="custom"
            className="col-lg-3 col-md-6"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3 }}
          >
            <div
              onClick={() => {
                setSelectedRole("custom");
                setShowCustomModal(true);
              }}
              className={`glass-card h-100 p-4 cursor-pointer ${
                selectedRole === "custom" ? "border-success border-2" : ""
              }`}
              style={{
                cursor: "pointer",
                background:
                  selectedRole === "custom"
                    ? "linear-gradient(135deg, rgba(16, 185, 129, 0.1) 0%, rgba(5, 150, 105, 0.05) 100%)"
                    : "rgba(255, 255, 255, 0.02)",
                transition: "all 0.3s ease",
              }}
            >
              <div
                className="d-inline-flex align-items-center justify-content-center rounded-3 p-3 mb-3"
                style={{ background: "rgba(16, 185, 129, 0.1)" }}
              >
                <div className="text-success">
                  <FiPlusCircle size={48} />
                </div>
              </div>
              <h3 className="h6 fw-bold mb-2">Custom Role</h3>
              <p className="text-muted small mb-3">
                Paste a JD or define your own role
              </p>
              <span className="badge bg-success bg-opacity-10 text-success small">
                Pro Feature
              </span>
            </div>
          </motion.div>
        </div>
      </div>

      {/* Custom Modal / Form Area */}
      {selectedRole === "custom" && (
        <motion.div
          initial={{ opacity: 0, height: 0 }}
          animate={{ opacity: 1, height: "auto" }}
          className="mb-5"
        >
          <div className="glass-card p-4 border border-success border-opacity-25">
            <h3 className="h5 fw-bold mb-3 text-success">
              Custom Interview Details
            </h3>
            <div className="row g-3">
              <div className="col-md-6">
                <label className="form-label small text-muted">
                  Job Title / Role
                </label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="e.g. Senior Python Data Scientist"
                  value={customRoleData.title}
                  onChange={(e) =>
                    setCustomRoleData({
                      ...customRoleData,
                      title: e.target.value,
                    })
                  }
                />
              </div>
              <div className="col-md-6">
                <label className="form-label small text-muted">
                  Focus Areas (Optional)
                </label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="e.g. AWS, Docker, System Design"
                  value={customRoleData.focusAreas}
                  onChange={(e) =>
                    setCustomRoleData({
                      ...customRoleData,
                      focusAreas: e.target.value,
                    })
                  }
                />
              </div>
              <div className="col-12">
                <label className="form-label small text-muted">
                  Job Description / Context
                </label>
                <textarea
                  className="form-control"
                  rows="4"
                  placeholder="Paste the job description or specific requirements here..."
                  value={customRoleData.jobDescription}
                  onChange={(e) =>
                    setCustomRoleData({
                      ...customRoleData,
                      jobDescription: e.target.value,
                    })
                  }
                ></textarea>
              </div>
            </div>
          </div>
        </motion.div>
      )}

      {/* Difficulty Selection */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.3 }}
        className="mb-5"
      >
        <h2 className="h4 fw-bold mb-4">Select Difficulty Level</h2>
        <div className="d-flex gap-3 justify-content-center">
          {difficultyLevels.map((level) => (
            <button
              key={level.id}
              onClick={() => setSelectedDifficulty(level.id)}
              className={`btn ${
                selectedDifficulty === level.id
                  ? "btn-primary"
                  : "btn-outline-light"
              } px-4`}
            >
              <div className="fw-bold">{level.label}</div>
              <div className="small opacity-75">{level.description}</div>
            </button>
          ))}
        </div>
      </motion.div>

      {/* Error Message */}
      {error && (
        <motion.div
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          className="alert alert-danger text-center mb-4"
        >
          {error}
        </motion.div>
      )}

      {/* Start Button */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.4 }}
        className="text-center"
      >
        <button
          onClick={handleStartInterview}
          disabled={!selectedRole || isCreating}
          className="btn btn-primary btn-lg px-5 rounded-pill"
        >
          {isCreating ? (
            <>
              <span className="spinner-border spinner-border-sm me-2" />
              Creating Session...
            </>
          ) : (
            <>
              Start Interview
              <FiArrowRight className="ms-2" />
            </>
          )}
        </button>
        <p className="text-muted small mt-3">
          {selectedRole === "custom"
            ? "Custom interviews consume 1 session credit"
            : user?.subscription === "free"
              ? "Free tier: 3 sessions/month"
              : "Unlimited sessions"}
        </p>
      </motion.div>
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import { useRouter } from "next/navigation";
import { motion } from "framer-motion";
import Link from "next/link";
import {
  FiCheckCircle,
  FiTrendingUp,
  FiMessageSquare,
  FiActivity,
  FiArrowLeft,
  FiRefreshCw,
  FiCode,
} from "react-icons/fi";
import { useSelector } from "react-redux";

export default function SessionSummaryPage({ params }) {
  const router = useRouter();
  const { user } = useSelector((state) => state.auth);
  const [session, setSession] = useState(null);
  const [summary, setSummary] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  const sessionId = params.id;

  useEffect(() => {
    if (!user) {
      router.push("/login");
      return;
    }

    fetchSessionDetails();
  }, [sessionId, user]);

  const fetchSessionDetails = async () => {
    try {
      // 1. Get Session Data (includes metrics & overall score)
      const sessionRes = await fetch(
        `${process.env.NEXT_PUBLIC_API_URL}/api/interview/sessions/${sessionId}`,
        {
          headers: { Authorization: `Bearer ${user.token}` },
        },
      );
      const sessionData = await sessionRes.json();

      // 2. Get Messages (to reconstruct summary if not saved - for MVP we'll just use session metrics)
      // Ideally, the 'end' endpoint returned the summary. Only if we store it can we retrieve it here.
      // For now, let's assume we Display metrics from Session and maybe re-fetch summary if we stored it
      // or just show the metrics.
      //
      // Let's actually update the UI to show the metrics we CAN get from the session object.

      if (sessionData.success) {
        setSession(sessionData.data);
      }
    } catch (error) {
      console.error("Fetch summary error:", error);
    } finally {
      setIsLoading(false);
    }
  };

  if (isLoading) {
    return (
      <div className="container py-5 mt-4 text-center">
        <div className="spinner-border text-primary" />
        <p className="mt-3 text-muted">Generating interview report...</p>
      </div>
    );
  }

  if (!session) {
    return (
      <div className="container py-5 mt-4 text-center">
        <h2 className="text-danger">Session not found</h2>
        <Link
          href="/tools/interview-simulator/dashboard"
          className="btn btn-primary mt-3"
        >
          Back to Dashboard
        </Link>
      </div>
    );
  }

  // Fallback for metrics if they don't exist yet
  const metrics = session.metrics || {
    technicalDepth: 0,
    clarity: 0,
    confidence: 0,
  };

  const getScoreColor = (score) => {
    if (score >= 8) return "text-success";
    if (score >= 5) return "text-warning";
    return "text-danger";
  };

  const getScoreBg = (score) => {
    if (score >= 8) return "bg-success";
    if (score >= 5) return "bg-warning";
    return "bg-danger";
  };

  return (
    <div className="container py-5 mt-4" style={{ maxWidth: "1000px" }}>
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="text-center mb-5"
      >
        <div className="d-inline-flex align-items-center justify-content-center p-3 rounded-circle bg-success bg-opacity-10 text-success mb-3">
          <FiCheckCircle size={48} />
        </div>
        <h1 className="h2 fw-bold mb-2">Interview Completed!</h1>
        <p className="text-muted">
          Here's how you performed in your{" "}
          <span className="text-primary fw-bold text-capitalize">
            {session.role === "custom" && session.customData?.title
              ? session.customData.title
              : session.role}
          </span>{" "}
          interview.
        </p>
      </motion.div>

      {/* Main Score Card */}
      <div className="row g-4 mb-5">
        <div className="col-md-4">
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.1 }}
            className="glass-card p-4 h-100 text-center d-flex flex-column justify-content-center"
          >
            <h3 className="h6 text-muted uppercase tracking-wider mb-3">
              Overall Score
            </h3>
            <div
              className={`display-1 fw-bold mb-2 ${getScoreColor(session.overallScore)}`}
            >
              {session.overallScore ? session.overallScore.toFixed(1) : "N/A"}
            </div>
            <div className="small text-muted">out of 10</div>
          </motion.div>
        </div>

        <div className="col-md-8">
          <motion.div
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.2 }}
            className="glass-card p-4 h-100"
          >
            <h3 className="h5 fw-bold mb-4">Performance Breakdown</h3>

            <div className="mb-4">
              <div className="d-flex justify-content-between mb-1">
                <span className="d-flex align-items-center gap-2">
                  <FiCode className="text-primary" /> Technical Depth
                </span>
                <span className="fw-bold">
                  {metrics.technicalDepth.toFixed(1)}/10
                </span>
              </div>
              <div className="progress" style={{ height: "10px" }}>
                <div
                  className={`progress-bar ${getScoreBg(metrics.technicalDepth)}`}
                  style={{ width: `${metrics.technicalDepth * 10}%` }}
                />
              </div>
            </div>

            <div className="mb-4">
              <div className="d-flex justify-content-between mb-1">
                <span className="d-flex align-items-center gap-2">
                  <FiMessageSquare className="text-info" /> Communication
                  Clarity
                </span>
                <span className="fw-bold">{metrics.clarity.toFixed(1)}/10</span>
              </div>
              <div className="progress" style={{ height: "10px" }}>
                <div
                  className={`progress-bar ${getScoreBg(metrics.clarity)}`}
                  style={{ width: `${metrics.clarity * 10}%` }}
                />
              </div>
            </div>

            <div>
              <div className="d-flex justify-content-between mb-1">
                <span className="d-flex align-items-center gap-2">
                  <FiActivity className="text-secondary" /> Confidence
                </span>
                <span className="fw-bold">
                  {metrics.confidence.toFixed(1)}/10
                </span>
              </div>
              <div className="progress" style={{ height: "10px" }}>
                <div
                  className={`progress-bar ${getScoreBg(metrics.confidence)}`}
                  style={{ width: `${metrics.confidence * 10}%` }}
                />
              </div>
            </div>
          </motion.div>
        </div>
      </div>

      {/* Actions */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.4 }}
        className="d-flex justify-content-center gap-3"
      >
        <Link
          href="/tools/interview-simulator/dashboard"
          className="btn btn-outline-light px-4"
        >
          <FiArrowLeft className="me-2" />
          Dashboard
        </Link>
        <Link
          href="/tools/interview-simulator/start"
          className="btn btn-primary px-4"
        >
          <FiRefreshCw className="me-2" />
          Start New Interview
        </Link>
      </motion.div>
    </div>
  );
}

