"use client";

import { motion } from "framer-motion";
import { useState, useEffect } from "react";
import { useSearchParams } from "next/navigation";
import {
  FiMail,
  FiUser,
  FiMessageSquare,
  FiDollarSign,
  FiCalendar,
  FiSend,
} from "react-icons/fi";

const serviceOptions = [
  { id: "full-stack", label: "Full-Stack Development" },
  { id: "performance", label: "Performance Optimization" },
  { id: "consulting", label: "Technical Consulting" },
  { id: "custom", label: "Custom Project" },
];

const budgetRanges = [
  "Under $2,500",
  "$2,500 - $5,000",
  "$5,000 - $10,000",
  "$10,000 - $25,000",
  "$25,000+",
];

const timelineOptions = [
  "ASAP (1-2 weeks)",
  "1 month",
  "2-3 months",
  "3+ months",
  "Flexible",
];

export default function ContactPage() {
  const searchParams = useSearchParams();
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    service: "",
    budget: "",
    timeline: "",
    message: "",
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState(null);

  useEffect(() => {
    const serviceParam = searchParams.get("service");
    const messageParam = searchParams.get("message");

    setFormData((prev) => ({
      ...prev,
      service: serviceParam || prev.service,
      message: messageParam || prev.message,
    }));
  }, [searchParams]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSubmitStatus(null);

    try {
      // Simulate API call - replace with actual endpoint
      await new Promise((resolve) => setTimeout(resolve, 1500));

      // TODO: Integrate with backend API
      console.log("Form submitted:", formData);

      setSubmitStatus("success");
      setFormData({
        name: "",
        email: "",
        service: "",
        budget: "",
        timeline: "",
        message: "",
      });
    } catch (error) {
      setSubmitStatus("error");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="container py-5 mt-4">
      <div className="row justify-content-center">
        <div className="col-lg-8">
          {/* Header */}
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="text-center mb-5"
          >
            <span className="badge bg-primary bg-opacity-10 text-primary border border-primary border-opacity-25 px-4 py-2 rounded-pill mb-3">
              Get In Touch
            </span>
            <h1 className="display-4 fw-bold mb-4">
              Let's Discuss Your <span className="text-primary">Project</span>
            </h1>
            <p className="lead text-muted">
              Fill out the form below and I'll get back to you within 24 hours.
            </p>
          </motion.div>

          {/* Contact Form */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2 }}
            className="glass-card p-4 p-md-5"
          >
            <form onSubmit={handleSubmit}>
              <div className="row g-4">
                {/* Name */}
                <div className="col-md-6">
                  <label className="form-label d-flex align-items-center gap-2">
                    <FiUser size={16} />
                    Full Name *
                  </label>
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    className="form-control form-control-lg bg-transparent"
                    placeholder="John Doe"
                    required
                  />
                </div>

                {/* Email */}
                <div className="col-md-6">
                  <label className="form-label d-flex align-items-center gap-2">
                    <FiMail size={16} />
                    Email Address *
                  </label>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    className="form-control form-control-lg bg-transparent"
                    placeholder="john@example.com"
                    required
                  />
                </div>

                {/* Service Type */}
                <div className="col-12">
                  <label className="form-label d-flex align-items-center gap-2">
                    <FiMessageSquare size={16} />
                    Service Interested In *
                  </label>
                  <select
                    name="service"
                    value={formData.service}
                    onChange={handleChange}
                    className="form-select form-select-lg bg-transparent"
                    required
                  >
                    <option value="" className="text-dark">
                      Select a service...
                    </option>
                    {serviceOptions.map((option) => (
                      <option
                        key={option.id}
                        value={option.id}
                        className="text-dark"
                      >
                        {option.label}
                      </option>
                    ))}
                  </select>
                </div>

                {/* Budget */}
                <div className="col-md-6">
                  <label className="form-label d-flex align-items-center gap-2">
                    <FiDollarSign size={16} />
                    Project Budget *
                  </label>
                  <select
                    name="budget"
                    value={formData.budget}
                    onChange={handleChange}
                    className="form-select form-select-lg bg-transparent"
                    required
                  >
                    <option value="" className="text-dark">
                      Select budget range...
                    </option>
                    {budgetRanges.map((range) => (
                      <option key={range} value={range} className="text-dark">
                        {range}
                      </option>
                    ))}
                  </select>
                </div>

                {/* Timeline */}
                <div className="col-md-6">
                  <label className="form-label d-flex align-items-center gap-2">
                    <FiCalendar size={16} />
                    Desired Timeline *
                  </label>
                  <select
                    name="timeline"
                    value={formData.timeline}
                    onChange={handleChange}
                    className="form-select form-select-lg bg-transparent"
                    required
                  >
                    <option value="" className="text-dark">
                      Select timeline...
                    </option>
                    {timelineOptions.map((option) => (
                      <option key={option} value={option} className="text-dark">
                        {option}
                      </option>
                    ))}
                  </select>
                </div>

                {/* Message */}
                <div className="col-12">
                  <label className="form-label d-flex align-items-center gap-2">
                    <FiMessageSquare size={16} />
                    Project Details *
                  </label>
                  <textarea
                    name="message"
                    value={formData.message}
                    onChange={handleChange}
                    className="form-control bg-transparent"
                    rows="6"
                    placeholder="Tell me about your project, goals, and any specific requirements..."
                    required
                  />
                </div>

                {/* Submit Button */}
                <div className="col-12">
                  <button
                    type="submit"
                    disabled={isSubmitting}
                    className="btn btn-primary btn-lg w-100 d-flex align-items-center justify-content-center gap-2"
                  >
                    {isSubmitting ? (
                      <>
                        <span
                          className="spinner-border spinner-border-sm"
                          role="status"
                          aria-hidden="true"
                        />
                        Sending...
                      </>
                    ) : (
                      <>
                        <FiSend />
                        Send Inquiry
                      </>
                    )}
                  </button>
                </div>

                {/* Status Messages */}
                {submitStatus === "success" && (
                  <div className="col-12">
                    <div className="alert alert-success d-flex align-items-center gap-2 mb-0">
                      <FiMessageSquare />
                      Thank you! I'll review your inquiry and get back to you
                      within 24 hours.
                    </div>
                  </div>
                )}

                {submitStatus === "error" && (
                  <div className="col-12">
                    <div className="alert alert-danger mb-0">
                      Something went wrong. Please try again or email me
                      directly.
                    </div>
                  </div>
                )}
              </div>
            </form>
          </motion.div>

          {/* Additional Contact Info */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.4 }}
            className="text-center mt-5"
          >
            <p className="text-muted">
              Prefer email? Reach me at{" "}
              <a
                href="mailto:hello@example.com"
                className="text-primary text-decoration-none"
              >
                hello@example.com
              </a>
            </p>
          </motion.div>
        </div>
      </div>
    </div>
  );
}

"use client";

import { motion } from "framer-motion";
import Link from "next/link";
import {
  FiCheck,
  FiArrowRight,
  FiCode,
  FiZap,
  FiTrendingUp,
} from "react-icons/fi";
import styles from "../../styles/ServicesProcess.module.scss";
import ServicesSlider from "@/components/services/ServicesSlider";

const services = [
  {
    id: "full-stack",
    title: "Full-Stack Development",
    icon: <FiCode size={32} />,
    description:
      "End-to-end web application development with modern tech stacks",
    features: [
      "React/Next.js frontend",
      "Node.js/Express backend",
      "MongoDB/PostgreSQL databases",
      "RESTful & GraphQL APIs",
      "Authentication & Authorization",
      "Deployment & DevOps",
    ],
    pricing: "Starting at $5,000",
    timeline: "4-8 weeks",
    popular: true,
  },
  {
    id: "performance",
    title: "Performance Optimization",
    icon: <FiZap size={32} />,
    description:
      "Speed up your existing applications and improve user experience",
    features: [
      "Lighthouse score optimization",
      "Code splitting & lazy loading",
      "Database query optimization",
      "CDN & caching strategies",
      "Bundle size reduction",
      "Core Web Vitals improvement",
    ],
    pricing: "Starting at $2,500",
    timeline: "2-4 weeks",
    popular: false,
  },
  {
    id: "consulting",
    title: "Technical Consulting",
    icon: <FiTrendingUp size={32} />,
    description:
      "Expert guidance on architecture, tech stack, and best practices",
    features: [
      "Architecture review",
      "Tech stack recommendations",
      "Code review & refactoring",
      "Team mentoring",
      "Performance audits",
      "Security assessments",
    ],
    pricing: "Starting at $150/hour",
    timeline: "Flexible",
    popular: false,
  },
];

export default function ServicesPage() {
  return (
    <div className="container py-5 mt-4">
      {/* Hero Section */}
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="text-center mb-5 py-5"
      >
        <span className="badge bg-primary bg-opacity-10 text-primary border border-primary border-opacity-25 px-4 py-2 rounded-pill mb-3">
          Professional Services
        </span>
        <h1 className={`display-3 fw-bold mb-4 ${styles.heroTitle}`}>
          Let&apos;s Build Something{" "}
          <span className={styles.heroSpan}>Amazing</span>
        </h1>
        <p
          className={`lead text-muted mx-auto ${styles.heroDesc}`}
          style={{ maxWidth: "700px" }}
        >
          Transform your ideas into high-performance, scalable applications.
          Choose a service package that fits your needs.
        </p>
      </motion.div>

      {/* Services Grid - Desktop Only */}
      <div className="row g-4 mb-5 d-none d-md-flex">
        {services.map((service, index) => (
          <motion.div
            key={service.id}
            className="col-lg-4 col-md-6"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
          >
            <div
              className={`glass-card h-100 p-4 position-relative ${service.popular ? "border-primary border-2" : ""}`}
              style={{
                background: service.popular
                  ? "linear-gradient(135deg, rgba(124, 58, 237, 0.05) 0%, rgba(168, 85, 247, 0.02) 100%)"
                  : "rgba(255, 255, 255, 0.02)",
              }}
            >
              {service.popular && (
                <div className="position-absolute top-0 end-0 m-3">
                  <span className="badge bg-primary px-3 py-2">
                    Most Popular
                  </span>
                </div>
              )}

              <div className="mb-4">
                <div
                  className="d-inline-flex align-items-center justify-content-center rounded-3 p-3 mb-3"
                  style={{ background: "rgba(124, 58, 237, 0.1)" }}
                >
                  <div className="text-primary">{service.icon}</div>
                </div>
                <h3 className="h4 fw-bold mb-2">{service.title}</h3>
                <p className="text-muted small mb-3">{service.description}</p>
              </div>

              <div className="mb-4">
                <div className="d-flex justify-content-between align-items-center mb-2">
                  <span className="text-muted small">Investment</span>
                  <span className="fw-bold text-primary">
                    {service.pricing}
                  </span>
                </div>
                <div className="d-flex justify-content-between align-items-center">
                  <span className="text-muted small">Timeline</span>
                  <span className="fw-medium">{service.timeline}</span>
                </div>
              </div>

              <div className="border-top border-white border-opacity-10 pt-4 mb-4">
                <h5 className="h6 fw-bold mb-3">What's Included:</h5>
                <ul className="list-unstyled">
                  {service.features.map((feature, idx) => (
                    <li
                      key={idx}
                      className="d-flex align-items-start gap-2 mb-2"
                    >
                      <FiCheck
                        className="text-success mt-1 flex-shrink-0"
                        size={16}
                      />
                      <span className="small">{feature}</span>
                    </li>
                  ))}
                </ul>
              </div>

              <Link
                href={`/contact?service=${service.id}`}
                className={`btn ${service.popular ? "btn-primary" : "btn-outline-light"} w-100 d-flex align-items-center justify-content-center gap-2`}
              >
                Get Started <FiArrowRight />
              </Link>
            </div>
          </motion.div>
        ))}
      </div>

      {/* Services Slider - Mobile Only */}
      <ServicesSlider services={services} />

      {/* Custom Projects CTA */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.4 }}
        className="glass-card p-5 text-center"
        style={{
          background:
            "linear-gradient(135deg, rgba(124, 58, 237, 0.1) 0%, rgba(168, 85, 247, 0.05) 100%)",
        }}
      >
        <h2 className="h3 fw-bold mb-3">Need Something Custom?</h2>
        <p
          className="text-muted mb-4"
          style={{ maxWidth: "600px", margin: "0 auto" }}
        >
          Every project is unique. Let's discuss your specific requirements and
          create a tailored solution that perfectly fits your needs.
        </p>
        <Link
          href="/contact"
          className="btn btn-primary btn-lg px-5 rounded-pill"
        >
          Schedule a Consultation
        </Link>
      </motion.div>

      <div className={styles.processContainer}>
        <h2 className="h3 fw-bold text-center mb-5">How It Works</h2>

        <div className={`position-relative ${styles.mobileScrollWrapper}`}>
          {/* Progress Line */}
          <div className={styles.progressLine}>
            <div className={styles.progressGradient} />
          </div>

          <div
            className={`row g-4 position-relative ${styles.processRow}`}
            style={{ zIndex: 1 }}
          >
            {[
              {
                step: "01",
                title: "Discovery",
                desc: "We'll define your core objectives, target audience, and functional requirements to ensure a perfect project fit.",
                icon: "ðŸ”",
              },
              {
                step: "02",
                title: "Proposal",
                desc: "A comprehensive roadmap covering feature scope, selected tech stack, and a clear, transparent project timeline.",
                icon: "ðŸ“",
              },
              {
                step: "03",
                title: "Development",
                desc: "Agile, transparent coding with continuous integration and regular milestones to keep your project on track.",
                icon: "ðŸ’»",
              },
              {
                step: "04",
                title: "Launch",
                desc: "Ensuring a flawless deployment followed by ongoing support and performance monitoring for long-term growth.",
                icon: "ðŸš€",
              },
            ].map((item, idx) => (
              <motion.div
                key={idx}
                className={`col-lg-3 col-md-6 ${styles.stepColumn}`}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.2 + idx * 0.1 }}
              >
                <div className={styles.stepCard}>
                  <div className={styles.stepCircle}>
                    <div className={styles.stepContent}>
                      <span className={styles.stepIcon}>{item.icon}</span>
                      <span className={styles.stepLabel}>STEP {item.step}</span>
                    </div>
                  </div>

                  <h4 className={styles.stepTitle}>{item.title}</h4>
                  <p className={styles.stepDesc}>{item.desc}</p>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

"use client";

import { useState, useEffect, useCallback } from "react";
import { useRouter } from "next/navigation";
import { motion } from "framer-motion";
import Link from "next/link";
import {
  FiCheckCircle,
  FiTrendingUp,
  FiMessageSquare,
  FiActivity,
  FiArrowLeft,
  FiRefreshCw,
  FiCode,
} from "react-icons/fi";
import { useSelector } from "react-redux";

export default function SessionSummaryPage({ params }) {
  const router = useRouter();
  const { user } = useSelector((state) => state.auth);
  const [session, setSession] = useState(null);
  const [summary, setSummary] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  const sessionId = params.id;

  useEffect(() => {
    if (!user) {
      router.push("/login");
      return;
    }

    fetchSessionDetails();
  }, [sessionId, user, router, fetchSessionDetails]);

  const fetchSessionDetails = useCallback(async () => {
    try {
      // 1. Get Session Data (includes metrics & overall score)
      const sessionRes = await fetch(
        `${process.env.NEXT_PUBLIC_API_URL}/api/interview/sessions/${sessionId}`,
        {
          headers: { Authorization: `Bearer ${user.token}` },
        },
      );
      const sessionData = await sessionRes.json();

      // 2. Get Messages (to reconstruct summary if not saved - for MVP we'll just use session metrics)
      // Ideally, the 'end' endpoint returned the summary. Only if we store it can we retrieve it here.
      // For now, let's assume we Display metrics from Session and maybe re-fetch summary if we stored it
      // or just show the metrics.
      //
      // Let's actually update the UI to show the metrics we CAN get from the session object.

      if (sessionData.success) {
        setSession(sessionData.data);
      }
    } catch (error) {
      console.error("Fetch summary error:", error);
    } finally {
      setIsLoading(false);
    }
  }, [sessionId, user]);

  if (isLoading) {
    return (
      <div className="container py-5 mt-4 text-center">
        <div className="spinner-border text-primary" />
        <p className="mt-3 text-muted">Generating interview report...</p>
      </div>
    );
  }

  if (!session) {
    return (
      <div className="container py-5 mt-4 text-center">
        <h2 className="text-danger">Session not found</h2>
        <Link
          href="/tools/interview-simulator/dashboard"
          className="btn btn-primary mt-3"
        >
          Back to Dashboard
        </Link>
      </div>
    );
  }

  // Fallback for metrics if they don't exist yet
  const metrics = session.metrics || {
    technicalDepth: 0,
    clarity: 0,
    confidence: 0,
  };

  const getScoreColor = (score) => {
    if (score >= 8) return "text-success";
    if (score >= 5) return "text-warning";
    return "text-danger";
  };

  const getScoreBg = (score) => {
    if (score >= 8) return "bg-success";
    if (score >= 5) return "bg-warning";
    return "bg-danger";
  };

  return (
    <div className="container py-5 mt-4" style={{ maxWidth: "1000px" }}>
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="text-center mb-5"
      >
        <div className="d-inline-flex align-items-center justify-content-center p-3 rounded-circle bg-success bg-opacity-10 text-success mb-3">
          <FiCheckCircle size={48} />
        </div>
        <h1 className="h2 fw-bold mb-2">Interview Completed!</h1>
        <p className="text-muted">
          Here's how you performed in your{" "}
          <span className="text-primary fw-bold text-capitalize">
            {session.role === "custom" && session.customData?.title
              ? session.customData.title
              : session.role}
          </span>{" "}
          interview.
        </p>
      </motion.div>

      {/* Main Score Card */}
      <div className="row g-4 mb-5">
        <div className="col-md-4">
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.1 }}
            className="glass-card p-4 h-100 text-center d-flex flex-column justify-content-center"
          >
            <h3 className="h6 text-muted uppercase tracking-wider mb-3">
              Overall Score
            </h3>
            <div
              className={`display-1 fw-bold mb-2 ${getScoreColor(session.overallScore)}`}
            >
              {session.overallScore ? session.overallScore.toFixed(1) : "N/A"}
            </div>
            <div className="small text-muted">out of 10</div>
          </motion.div>
        </div>

        <div className="col-md-8">
          <motion.div
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.2 }}
            className="glass-card p-4 h-100"
          >
            <h3 className="h5 fw-bold mb-4">Performance Breakdown</h3>

            <div className="mb-4">
              <div className="d-flex justify-content-between mb-1">
                <span className="d-flex align-items-center gap-2">
                  <FiCode className="text-primary" /> Technical Depth
                </span>
                <span className="fw-bold">
                  {metrics.technicalDepth.toFixed(1)}/10
                </span>
              </div>
              <div className="progress" style={{ height: "10px" }}>
                <div
                  className={`progress-bar ${getScoreBg(metrics.technicalDepth)}`}
                  style={{ width: `${metrics.technicalDepth * 10}%` }}
                />
              </div>
            </div>

            <div className="mb-4">
              <div className="d-flex justify-content-between mb-1">
                <span className="d-flex align-items-center gap-2">
                  <FiMessageSquare className="text-info" /> Communication
                  Clarity
                </span>
                <span className="fw-bold">{metrics.clarity.toFixed(1)}/10</span>
              </div>
              <div className="progress" style={{ height: "10px" }}>
                <div
                  className={`progress-bar ${getScoreBg(metrics.clarity)}`}
                  style={{ width: `${metrics.clarity * 10}%` }}
                />
              </div>
            </div>

            <div>
              <div className="d-flex justify-content-between mb-1">
                <span className="d-flex align-items-center gap-2">
                  <FiActivity className="text-secondary" /> Confidence
                </span>
                <span className="fw-bold">
                  {metrics.confidence.toFixed(1)}/10
                </span>
              </div>
              <div className="progress" style={{ height: "10px" }}>
                <div
                  className={`progress-bar ${getScoreBg(metrics.confidence)}`}
                  style={{ width: `${metrics.confidence * 10}%` }}
                />
              </div>
            </div>
          </motion.div>
        </div>
      </div>

      {/* Actions */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.4 }}
        className="d-flex justify-content-center gap-3"
      >
        <Link
          href="/tools/interview-simulator/dashboard"
          className="btn btn-outline-light px-4"
        >
          <FiArrowLeft className="me-2" />
          Dashboard
        </Link>
        <Link
          href="/tools/interview-simulator/start"
          className="btn btn-primary px-4"
        >
          <FiRefreshCw className="me-2" />
          Start New Interview
        </Link>
      </motion.div>
    </div>
  );
}

"use client";

import { useEffect, useRef, useState } from "react";
import dynamic from "next/dynamic";
import Link from "next/link";
import { FiCheck, FiArrowRight } from "react-icons/fi";
import { FaChevronLeft, FaChevronRight } from "react-icons/fa";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Slider = dynamic(() => import("react-slick"), { ssr: false });

function PrevArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{
        ...style,
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        background: "rgba(124, 58, 237, 0.15)",
        borderRadius: "50%",
        width: "38px",
        height: "38px",
        zIndex: 5,
        left: "5px",
        backdropFilter: "blur(10px)",
        border: "1.5px solid rgba(124, 58, 237, 0.4)",
        boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
      }}
      onClick={onClick}
    >
      <FaChevronLeft color="#7c3aed" size={18} />
    </div>
  );
}

function NextArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{
        ...style,
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        background: "rgba(124, 58, 237, 0.15)",
        borderRadius: "50%",
        width: "38px",
        height: "38px",
        zIndex: 5,
        right: "5px",
        backdropFilter: "blur(10px)",
        border: "1.5px solid rgba(124, 58, 237, 0.4)",
        boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
      }}
      onClick={onClick}
    >
      <FaChevronRight color="#7c3aed" size={18} />
    </div>
  );
}

export default function ServicesSlider({ services }) {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isReady, setIsReady] = useState(false);
  const containerRef = useRef(null);

  useEffect(() => {
    if (!containerRef.current) return;
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsReady(true);
          observer.disconnect();
        }
      },
      { rootMargin: "200px" },
    );
    observer.observe(containerRef.current);
    return () => observer.disconnect();
  }, []);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    centerMode: true,
    centerPadding: "15%",
    arrows: true,
    prevArrow: <PrevArrow />,
    nextArrow: <NextArrow />,
    beforeChange: (oldIndex, newIndex) => setCurrentSlide(newIndex),
    appendDots: (dots) => (
      <div className="slick-custom-dots-wrapper">
        <ul className="m-0 p-0 slick-custom-dots">{dots}</ul>
      </div>
    ),
    customPaging: (i) => (
      <div
        className={`custom-dot-inner ${i === currentSlide ? "active" : ""}`}
      />
    ),
    responsive: [
      {
        breakpoint: 480,
        settings: {
          centerPadding: "24px",
          slidesToShow: 1,
        },
      },
    ],
  };

  return (
    <div
      ref={containerRef}
      className="d-md-none slick-slider-container mb-5 w-100"
    >
      {isReady ? (
        <Slider {...settings}>
          {services.map((service, idx) => {
            const isActive = idx === currentSlide;
            return (
              <div key={service.id} className="px-2 py-4">
                <div
                  className={`glass-card p-4 position-relative transition-all h-100 ${
                    isActive ? "active-focus-card" : "opacity-75"
                  }`}
                  style={{
                    minHeight: "550px", // Enough to fit all content
                    background: service.popular
                      ? isActive
                        ? "rgba(15, 23, 42, 0.95)"
                        : "linear-gradient(135deg, rgba(124, 58, 237, 0.05) 0%, rgba(168, 85, 247, 0.02) 100%)"
                      : "rgba(255, 255, 255, 0.02)",
                    transform: isActive
                      ? "scale(1.05) translateY(-5px)"
                      : "scale(0.9)",
                    boxShadow: isActive
                      ? "0 25px 60px rgba(124, 58, 237, 0.4)"
                      : "none",
                  }}
                >
                  {service.popular && (
                    <div className="position-absolute top-0 end-0 m-3">
                      <span className="badge bg-primary px-3 py-2">
                        Most Popular
                      </span>
                    </div>
                  )}

                  <div className="mb-4">
                    <div
                      className="d-inline-flex align-items-center justify-content-center rounded-3 p-3 mb-3"
                      style={{ background: "rgba(124, 58, 237, 0.15)" }}
                    >
                      <div className="text-primary">{service.icon}</div>
                    </div>
                    <h3 className="h4 fw-bold mb-2">{service.title}</h3>
                    <p className="text-muted small mb-3">
                      {service.description}
                    </p>
                  </div>

                  <div className="mb-4">
                    <div className="mb-3">
                      <div className="text-muted small mb-1 text-start">
                        Investment
                      </div>
                      <div className="fw-bold text-primary h5 mb-0 text-start">
                        {service.pricing}
                      </div>
                    </div>
                    <div>
                      <div className="text-muted small mb-1 text-start">
                        Timeline
                      </div>
                      <div className="fw-medium text-start">
                        {service.timeline}
                      </div>
                    </div>
                  </div>

                  <div className="border-top border-white border-opacity-10 pt-4 mb-4">
                    <h5 className="h6 fw-bold mb-3 text-start">
                      What's Included:
                    </h5>
                    <ul className="list-unstyled text-start">
                      {service.features.map((feature, fIdx) => (
                        <li
                          key={fIdx}
                          className="d-flex align-items-start gap-2 mb-2"
                        >
                          <FiCheck
                            className="text-success mt-1 flex-shrink-0"
                            size={16}
                          />
                          <span className="small">{feature}</span>
                        </li>
                      ))}
                    </ul>
                  </div>

                  <Link
                    href={`/contact?service=${service.id}`}
                    className={`btn ${service.popular ? "btn-primary" : "btn-outline-light"} w-100 d-flex align-items-center justify-content-center gap-2 mt-auto`}
                  >
                    Get Started <FiArrowRight />
                  </Link>
                </div>
              </div>
            );
          })}
        </Slider>
      ) : (
        <div className="glass-card text-center p-4">
          <div className="text-muted">Loading services...</div>
        </div>
      )}
    </div>
  );
}
