        }
      `}</style>
    </div>
  );
}

"use client";

import { motion } from "framer-motion";
import Link from "next/link";
import { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import {
  useGetBlogsQuery,
  useUpdateBlogDeleteStatusMutation,
} from "@/store/services/blogsApi";
import { useRouter } from "next/navigation";
import Swal from "sweetalert2";
import {
  FiEye,
  FiEdit2,
  FiTrash2,
  FiPlus,
  FiCalendar,
  FiBookOpen,
} from "react-icons/fi";

export default function BlogsPage() {
  const { user } = useSelector((state) => state.auth);
  const { data: blogsData, error, isLoading } = useGetBlogsQuery();
  const [deleteBlog] = useUpdateBlogDeleteStatusMutation();
  const { isAuthenticated } = useSelector((state) => state.auth);
  const [isMounted, setIsMounted] = useState(false);
  const router = useRouter();

  const blogs = blogsData?.data || [];
  const role = user?.role;

  useEffect(() => {
    setIsMounted(true);
  }, []);

  const handleEdit = (id) => {
    router.push(`/dashboard?tab=blogs&edit=${id}`);
  };

  const handleView = (id) => {
    router.push(`/blogs/${id}`);
  };

  const handleDelete = async (id) => {
    try {
      const result = await Swal.fire({
        title: "Delete Blog?",
        text: "This action cannot be undone.",
        icon: "warning",
        showCancelButton: true,
        background: "var(--glass-bg)",
        color: "var(--text-color)",
        confirmButtonColor: "#ef4444",
        cancelButtonColor: "transparent",
        confirmButtonText: "Yes, delete it!",
        customClass: {
          popup: "glass-card border-0",
          confirmButton: "btn btn-danger px-4 rounded-pill",
          cancelButton: "btn btn-outline-light px-4 rounded-pill",
        },
      });

      if (result.isConfirmed) {
        await deleteBlog(id).unwrap();
        Swal.fire({
          title: "Deleted!",
          text: "Blog has been removed.",
          icon: "success",
          background: "var(--glass-bg)",
          color: "var(--text-color)",
          timer: 2000,
          showConfirmButton: false,
        });
      }
    } catch (error) {
      console.error("Failed to delete blog:", error);
      Swal.fire("Error!", "Failed to delete blog.", "error");
    }
  };

  // Helper to strip HTML tags for preview
  const stripHtml = (html) => {
    let tmp = document.createElement("DIV");
    tmp.innerHTML = html;
    return tmp.textContent || tmp.innerText || "";
  };

  return (
    <div className="container py-5 mt-4">
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="mb-5"
      >
        <div className="row align-items-center g-4">
          <div className="col-lg-8 text-center text-lg-start">
            <h1 className="fw-bold display-4 mb-3">
              Latest <span className="text-secondary">Insights</span>
            </h1>
            <p className="lead text-muted max-w-2xl mx-auto mx-lg-0">
              Thoughts, tutorials, and updates from the world of development.
            </p>
          </div>

          <div className="col-lg-4 text-center text-lg-end">
            {isMounted && isAuthenticated && role === "admin" && (
              <Link
                href="/dashboard?tab=blogs"
                className="btn btn-secondary btn-lg rounded-pill px-5 py-3 shadow-lg hover-lift d-inline-flex align-items-center gap-2"
              >
                <FiPlus /> Write New Blog
              </Link>
            )}
          </div>
        </div>
      </motion.div>

      {isLoading && (
        <div className="d-flex flex-column align-items-center justify-content-center py-5">
          <div
            className="spinner-border text-secondary mb-3"
            style={{ width: "3rem", height: "3rem" }}
            role="status"
          />
          <p className="text-muted">Loading articles...</p>
        </div>
      )}

      {error && (
        <div className="glass-card p-5 text-center border-danger border-opacity-25">
          <FiBookOpen size={48} className="text-danger mb-3 opacity-50" />
          <h3 className="h5 fw-bold">Connection Issue</h3>
          <p className="text-muted small">
            Unable to load blogs at the moment. Please try again soon.
          </p>
        </div>
      )}

      {!isLoading && blogs.length === 0 && !error && (
        <div className="glass-card p-5 text-center border-opacity-10">
          <FiBookOpen size={48} className="text-muted mb-3 opacity-25" />
          <h3 className="h5 fw-bold">No Blogs Yet</h3>
          <p className="text-muted small">
            Stay tuned! Content is coming soon.
          </p>
        </div>
      )}

      {blogs.length > 0 && (
        <div className="row g-4 lg:g-5">
          {blogs.map((blog, index) => (
            <motion.div
              key={blog._id}
              className="col-lg-4 col-md-6"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
            >
              <div className="glass-card h-100 p-4 d-flex flex-column group hover-lift border-1 border-white border-opacity-5">
                {/* Header Actions */}
                <div className="d-flex justify-content-between align-items-center mb-4">
                  <div className="d-flex align-items-center gap-2 text-muted small">
                    <FiCalendar />
                    {new Date(blog.createdAt).toLocaleDateString(undefined, {
                      month: "short",
                      day: "numeric",
                      year: "numeric",
                    })}
                  </div>

                  <div className="d-flex gap-2">
                    {/* View Button */}
                    <button
                      onClick={() => handleView(blog._id)}
                      className="btn btn-glass btn-icon-round"
                      title="Read More"
                    >
                      <FiEye size={18} />
                    </button>
                    {role === "admin" && (
                      <>
                        <button
                          onClick={() => handleEdit(blog._id)}
                          className="btn btn-glass btn-icon-round text-info"
                          title="Edit"
                        >
                          <FiEdit2 size={16} />
                        </button>
                        <button
                          onClick={() => handleDelete(blog._id)}
                          className="btn btn-glass btn-icon-round text-danger"
                          title="Delete"
                        >
                          <FiTrash2 size={16} />
                        </button>
                      </>
                    )}
                  </div>
                </div>

                <div className="flex-grow-1">
                  <div className="mb-2">
                    <span
                      className={`badge ${blog.status === "Published" ? "bg-success bg-opacity-10 text-success" : "bg-warning bg-opacity-10 text-warning"}`}
                    >
                      {blog.status}
                    </span>
                  </div>
                  {blog.image && (
                    <div className="mb-3">
                      <img
                        src={blog.image}
                        alt={blog.title}
                        className="img-fluid rounded-3"
                        style={{
                          objectFit: "cover",
                          width: "100%",
                          height: "200px",
                        }}
                      />
                    </div>
                  )}
                  <h3 className="h4 fw-bold mb-3 project-title-hover">
                    {blog?.title}
                  </h3>

                  <p className="small text-muted mb-4 line-clamp-3">
                    {blog?.subheading ||
                      (isMounted
                        ? stripHtml(blog.body).substring(0, 100) + "..."
                        : "Reading...")}
                  </p>
                </div>

                <div className="mt-auto pt-4 border-top border-white border-opacity-5 text-end">
                  <Link
                    href={`/blogs/${blog._id}`}
                    className="btn btn-link btn-sm text-secondary text-decoration-none fw-bold p-0"
                  >
                    Read Article →
                  </Link>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      )}
    </div>
  );
}

"use client";

import { Suspense } from "react";
import React, { useState, useEffect } from "react";
import Link from "next/link";
import RichTextEditor from "./RichTextEditor";
import ProtectedRoute from "@/components/auth/ProtectedRoute";
import { useSelector, useDispatch } from "react-redux";
import { useForm } from "react-hook-form";
import { updateProfile } from "@/store/slices/contentSlice";
import {
  useGetProjectsQuery,
  useGetProjectQuery,
  useAddProjectMutation,
  useUpdateDeleteStatusMutation,
  useUpdateProjectMutation,
} from "@/store/services/projectsApi";
import {
  useGetAnalyticsStatsQuery,
  useGetAnalyticsSessionsQuery,
} from "@/store/services/analyticsApi";
import {
  useGetBlogsQuery,
  useGetBlogQuery,
  useAddBlogMutation,
  useUpdateBlogMutation,
  useUpdateBlogDeleteStatusMutation,
  useUploadImageMutation,
} from "@/store/services/blogsApi";

import { useSearchParams, useRouter } from "next/navigation";
import Swal from "sweetalert2";

const Toast = Swal.mixin({
  toast: true,
  position: "top-end",
  showConfirmButton: false,
  timer: 3000,
  timerProgressBar: true,
  didOpen: (toast) => {
    toast.onmouseenter = Swal.stopTimer;
    toast.onmouseleave = Swal.resumeTimer;
  },
  background: "rgba(255, 255, 255, 0.9)",
  color: "#333",
});

function DashboardContent() {
  const { user } = useSelector((state) => state.auth);
  const { profile } = useSelector((state) => state.content);
  const dispatch = useDispatch();
  const searchParams = useSearchParams();
  const router = useRouter();

  const { data: projectsData, isLoading: isProjectsLoading } =
    useGetProjectsQuery();
  const { data: blogsData, isLoading: isBlogsLoading } = useGetBlogsQuery();
  const [analyticsWindow, setAnalyticsWindow] = useState("7d");
  const { data: analyticsStatsData } = useGetAnalyticsStatsQuery(
    { window: analyticsWindow },
    {
      skip: user?.role !== "admin",
    },
  );
  const { data: analyticsSessionsData, isLoading: isSessionsLoading } =
    useGetAnalyticsSessionsQuery(
      { page: 1, limit: 20 },
      { skip: user?.role !== "admin" },
    );
  const [addProject, { isLoading: isAdding }] = useAddProjectMutation();
  const [updateProject, { isLoading: isUpdating }] = useUpdateProjectMutation();
  const [deleteProject] = useUpdateDeleteStatusMutation();

  const [addBlog, { isLoading: isAddingBlog }] = useAddBlogMutation();
  const [updateBlog, { isLoading: isUpdatingBlog }] = useUpdateBlogMutation();
  const [deleteBlog] = useUpdateBlogDeleteStatusMutation();
  const [uploadImage, { isLoading: isUploadingImage }] =
    useUploadImageMutation();

  const projects = projectsData?.data || [];
  const blogs = blogsData?.data || [];

  const { register: registerProfile, handleSubmit: handleSubmitProfile } =
    useForm({
      defaultValues: profile,
    });

  const {
    register: registerProject,
    handleSubmit: handleSubmitProject,
    reset: resetProject,
    setValue: setProjectValue,
    watch: watchProject,
  } = useForm();
  const [showProjectEditor, setShowProjectEditor] = useState(false);
  const projectBody = watchProject("body");
  const [editingProject, setEditingProject] = useState(null);

  const {
    register: registerBlog,
    handleSubmit: handleSubmitBlog,
    reset: resetBlog,
    setValue: setBlogValue,
    watch: watchBlog,
  } = useForm();
  const [showBlogEditor, setShowBlogEditor] = useState(false);
  const blogBody = watchBlog("body");
  const [editingBlog, setEditingBlog] = useState(null);
  const [blogImagePreview, setBlogImagePreview] = useState(null);

  // Tab State
  const tab = searchParams.get("tab") || "projects";

  // Check for edit/view params
  const editId = searchParams.get("edit");
  const viewId = searchParams.get("view");
  const targetId = editId || viewId;

  const { data: projectToEditData } = useGetProjectQuery(targetId, {
    skip: !targetId || tab !== "projects",
  });

  const { data: blogToEditData } = useGetBlogQuery(targetId, {
    skip: !targetId || tab !== "blogs",
  });

  useEffect(() => {
    if (targetId && projectToEditData?.data) {
      const projectToEdit = projectToEditData.data;
      setEditingProject(projectToEdit);
      setProjectValue("title", projectToEdit.title);
      setProjectValue("subheading", projectToEdit.subheading || "");
      setProjectValue("status", projectToEdit.status);
      setProjectValue("body", projectToEdit.body);
      setProjectValue("description", projectToEdit.description);
      setShowProjectEditor(true);
      window.scrollTo({ top: 0, behavior: "smooth" });
    }
  }, [targetId, projectToEditData, setProjectValue]);

  useEffect(() => {
    if (targetId && tab === "blogs" && blogToEditData?.data) {
      const blogToEdit = blogToEditData.data;
      setEditingBlog(blogToEdit);
      setBlogValue("title", blogToEdit.title);
      setBlogValue("subheading", blogToEdit.subheading || "");
      setBlogValue("status", blogToEdit.status);
      setBlogValue("body", blogToEdit.body);
      setBlogImagePreview(blogToEdit.image);
      setShowBlogEditor(true);
      window.scrollTo({ top: 0, behavior: "smooth" });
    }
  }, [targetId, tab, blogToEditData, setBlogValue]);

  // Handle Blog Image Preview for local files
  const watchedBlogImage = watchBlog("image");
  useEffect(() => {
    if (watchedBlogImage && watchedBlogImage[0] instanceof File) {
      const imageUrl = URL.createObjectURL(watchedBlogImage[0]);
      setBlogImagePreview(imageUrl);
      // Clean up the URL when component unmounts or image changes
      return () => URL.revokeObjectURL(imageUrl);
    }
  }, [watchedBlogImage]);

  const onUpdateProfile = (data) => {
    dispatch(updateProfile(data));
    Toast.fire({
      icon: "success",
      title: "Profile Updated successfully",
    });
  };

  const onSubmitProject = async (data) => {
    try {
      if (editingProject) {
        await updateProject({ id: editingProject._id, ...data }).unwrap();
        Toast.fire({
          icon: "success",
          title: "Project Updated successfully",
        });
        setEditingProject(null);
        // Navigate to the public projects page as requested
        router.push("/projects");
      } else {
        await addProject(data).unwrap();
        Toast.fire({
          icon: "success",
          title: "Project Added successfully",
        });
        // Remove this router.push since we are already on the dashboard and want to stay or reset
        router.push("/projects");
      }
      resetProject();
      setShowProjectEditor(false);
    } catch (err) {
      console.error("Failed to save project:", err);
      const errorMessage = err?.data?.error
        ? Array.isArray(err.data.error)
          ? err.data.error.join("\n")
          : err.data.error
        : "Failed to save project";

      Toast.fire({
        icon: "error",
        title: errorMessage,
      });
    }
  };

  const handleCancelEdit = () => {
    setEditingProject(null);
    resetProject();
    setShowProjectEditor(false);
    router.push("/dashboard");
  };

  const handleDeleteProject = async (id) => {
    if (window.confirm("Are you sure you want to delete this project?")) {
      try {
        await deleteProject(id).unwrap();
        if (editingProject?._id === id) {
          handleCancelEdit();
        }
      } catch (err) {
        console.error("Failed to delete project:", err);
      }
    }
  };

  const handleEditClick = (project) => {
    router.push(`/dashboard?edit=${project._id}`);
  };

  const onSubmitBlog = async (data) => {
    try {
      let finalData = { ...data };

      // Handle image upload if a file is selected
      if (data.image && data.image[0] instanceof File) {
        const formData = new FormData();
        formData.append("image", data.image[0]);
        const uploadResult = await uploadImage(formData).unwrap();
        if (uploadResult.success) {
          finalData.image = uploadResult.url;
        }
      } else if (editingBlog && editingBlog.image) {
        // Preserve existing image URL if editing and no new image is selected
        finalData.image = editingBlog.image;
      } else {
        // Remove image field if it's an empty FileList/object to prevent backend errors
        delete finalData.image;
      }

      if (editingBlog) {
        await updateBlog({ id: editingBlog._id, ...finalData }).unwrap();
        Toast.fire({
          icon: "success",
          title: "Blog Updated successfully",
        });
        setEditingBlog(null);
        router.push("/blogs");
      } else {
        await addBlog(finalData).unwrap();
        Toast.fire({
          icon: "success",
          title: "Blog Added successfully",
        });
        router.push("/blogs");
      }
      resetBlog();
      setBlogImagePreview(null);
      setShowBlogEditor(false);
    } catch (err) {
      console.error("Failed to save blog:", err);
      // Helper to extract error message
      let errorMessage = "Failed to save blog";
      if (err?.data?.message) {
        errorMessage = err.data.message;
      } else if (err?.data?.error) {
        errorMessage = Array.isArray(err.data.error)
          ? err.data.error.join("\n")
          : err.data.error;
      } else if (err?.error) {
        errorMessage = err.error;
      }

      Toast.fire({
        icon: "error",
        title: errorMessage,
      });
    }
  };

  const handleCancelEditBlog = () => {
    setEditingBlog(null);
    resetBlog();
    setBlogImagePreview(null);
    setShowBlogEditor(false);
    router.push("/dashboard?tab=blogs");
  };

  const handleDeleteBlog = async (id) => {
    if (window.confirm("Are you sure you want to delete this blog?")) {
      try {
        await deleteBlog(id).unwrap();
        if (editingBlog?._id === id) {
          handleCancelEditBlog();
        }
      } catch (err) {
        console.error("Failed to delete blog:", err);
      }
    }
  };

  const handleEditBlogClick = (blog) => {
    router.push(`/dashboard?tab=blogs&edit=${blog._id}`);
  };

  const stats = {
    views: analyticsStatsData?.data?.totalViews || 0,
    projects: projects.length,
    messages: 45,
    avgTimeSeconds: analyticsStatsData?.data?.avgTimeSeconds || 0,
    viewsChangePct: analyticsStatsData?.data?.viewsChangePct ?? 0,
    projectsChangePct: analyticsStatsData?.data?.projectsChangePct ?? 0,
  };

  return (
    <ProtectedRoute>
      <div className="container py-5">
        <div className="row mb-5">
          <div className="col">
            <h1 className="fw-bold">Admin Dashboard</h1>
            <p className="text-muted mb-4">
              Welcome, {user?.name} ({user?.role})
            </p>

            {user?.role === "admin" && (
              <div className="mb-5">
                <Link href="/dashboard/sub-users" className="btn btn-primary">
                  Manage Sub-Users
                </Link>
              </div>
            )}

            {/* Tab Navigation */}
            <div className="d-flex gap-3 mb-4">
              <button
                className={`btn ${tab === "projects" ? "btn-primary" : "btn-outline-light"}`}
                onClick={() => router.push("/dashboard?tab=projects")}
              >
                Projects
              </button>
              <button
                className={`btn ${tab === "blogs" ? "btn-primary" : "btn-outline-light"}`}
                onClick={() => router.push("/dashboard?tab=blogs")}
              >
                Blogs
              </button>
            </div>

            {user?.role === "admin" && (
              <div className="d-flex flex-column gap-5">
                <div className="glass-card p-4">
                  <h4 className="mb-3">Edit Profile</h4>
                  <form onSubmit={handleSubmitProfile(onUpdateProfile)}>
                    <div className="row g-3">
                      <div className="col-md-6">
                        <input
                          {...registerProfile("name")}
                          className="form-control bg-transparent"
                          placeholder="Full Name"
                        />
                      </div>
                      <div className="col-md-6">
                        <input
                          {...registerProfile("title")}
                          className="form-control bg-transparent"
                          placeholder="Job Title"
                        />
                      </div>
                      <div className="col-12">
                        <textarea
                          {...registerProfile("bio")}
                          className="form-control bg-transparent"
                          placeholder="Bio"
                          rows="3"
                        ></textarea>
                      </div>
                      <div className="col-12 text-end">
                        <button type="submit" className="btn btn-primary">
                          Update Profile
                        </button>
                      </div>
                    </div>
                  </form>
                </div>

                {tab === "projects" && (
                  <div className="glass-card p-4">
                    <div className="d-flex justify-content-between align-items-center mb-3">
                      <h4 className="mb-0">
                        {editingProject ? "Edit Project" : "Add New Project"}
                      </h4>
                      {editingProject && (
                        <button
                          onClick={handleCancelEdit}
                          className="btn btn-outline-light btn-sm"
                        >
                          Cancel Edit
                        </button>
                      )}
                    </div>
                    <form onSubmit={handleSubmitProject(onSubmitProject)}>
                      <div className="row g-3">
                        <div className="col-md-6">
                          <input
                            {...registerProject("title")}
                            className="form-control bg-transparent"
                            placeholder="Project Title"
                            required
                          />
                        </div>
                        <div className="col-md-6">
                          <select
                            {...registerProject("status")}
                            className="form-select bg-transparent"
                          >
                            <option className="text-dark" value="In Progress">
                              In Progress
                            </option>
                            <option className="text-dark" value="Completed">
                              Completed
                            </option>
                          </select>
                        </div>
                        <div className="col-md-12">
                          <input
                            {...registerProject("subheading")}
                            className="form-control bg-transparent"
                            placeholder="Subheading (Short Description)"
                          />
                        </div>
                        <div className="col-12">
                          <label className="form-label">Description</label>
                          <div
                            className="form-control bg-transparent"
                            style={{
                              minHeight: "100px",
                              cursor: "pointer",
                              border: "1px solid rgba(255, 255, 255, 0.1)",
                              background: "rgba(0,0,0,0.1) !important",
                            }}
                            onClick={() => setShowProjectEditor(true)}
                          >
                            {projectBody ? (
                              <div
                                dangerouslySetInnerHTML={{
                                  __html: projectBody,
                                }}
                              />
                            ) : (
                              <span className="text-secondary opacity-50">
                                Click to add description...
                              </span>
                            )}
                          </div>
                          <input type="hidden" {...registerProject("body")} />
                        </div>
                        {showProjectEditor && (
                          <RichTextEditor
                            value={projectBody}
                            onChange={(content) =>
                              setProjectValue("body", content)
                            }
                            onClose={() => setShowProjectEditor(false)}
                          />
                        )}
                        <div className="col-12 text-end">
                          <button
                            type="submit"
                            className="btn btn-success"
                            disabled={isAdding || isUpdating}
                          >
                            {isAdding || isUpdating
                              ? editingProject
                                ? "Updating..."
                                : "Adding..."
                              : editingProject
                                ? "Update Project"
                                : "Add Project"}
                          </button>
                        </div>
                      </div>
                    </form>
                  </div>
                )}

                {tab === "blogs" && (
                  <div className="glass-card p-4">
                    <div className="d-flex justify-content-between align-items-center mb-3">
                      <h4 className="mb-0">
                        {editingBlog ? "Edit Blog" : "Add New Blog"}
                      </h4>
                      {editingBlog && (
                        <button
                          onClick={handleCancelEditBlog}
                          className="btn btn-outline-light btn-sm"
                        >
                          Cancel Edit
                        </button>
                      )}
                    </div>
                    <form onSubmit={handleSubmitBlog(onSubmitBlog)}>
                      <div className="row g-3">
                        <div className="col-md-6">
                          <input
                            {...registerBlog("title")}
                            className="form-control bg-transparent"
                            placeholder="Blog Title"
                            required
                          />
                        </div>
                        <div className="col-md-6">
                          <select
                            {...registerBlog("status")}
                            className="form-select bg-transparent"
                          >
                            <option className="text-dark" value="Draft">
                              Draft
                            </option>
                            <option className="text-dark" value="Published">
                              Published
                            </option>
                          </select>
                        </div>
                        <div className="col-md-12">
                          <label className="form-label">Blog Image</label>
                          {blogImagePreview && (
                            <div className="mb-3">
                              <img
                                src={blogImagePreview}
                                alt="Blog Preview"
                                className="img-thumbnail bg-transparent border-secondary"
                                style={{
                                  maxHeight: "200px",
                                  objectFit: "cover",
                                }}
                              />
                            </div>
                          )}
                          <input
                            type="file"
                            {...registerBlog("image")}
                            className="form-control bg-transparent"
                            accept="image/*"
                          />
                        </div>
                        <div className="col-md-12">
                          <input
                            {...registerBlog("subheading")}
                            className="form-control bg-transparent"
                            placeholder="Subheading (Short Description)"
                          />
                        </div>
                        <div className="col-12">
                          <label className="form-label">Body</label>
                          <div
                            className="form-control bg-transparent"
                            style={{
                              minHeight: "100px",
                              cursor: "pointer",
                              border: "1px solid rgba(255, 255, 255, 0.1)",
                              background: "rgba(0,0,0,0.1) !important",
                            }}
                            onClick={() => setShowBlogEditor(true)}
                          >
                            {blogBody ? (
                              <div
                                dangerouslySetInnerHTML={{ __html: blogBody }}
                              />
                            ) : (
                              <span className="text-secondary opacity-50">
                                Click to add body content...
                              </span>
                            )}
                          </div>
                          <input type="hidden" {...registerBlog("body")} />
                        </div>
                        {showBlogEditor && (
                          <RichTextEditor
                            value={blogBody}
                            onChange={(content) =>
                              setBlogValue("body", content)
                            }
                            onClose={() => setShowBlogEditor(false)}
                          />
                        )}
                        <div className="col-12 text-end">
                          <button
                            type="submit"
                            className="btn btn-success"
                            disabled={
                              isAddingBlog || isUpdatingBlog || isUploadingImage
                            }
                          >
                            {isUploadingImage ? (
                              <>
                                <span
                                  className="spinner-border spinner-border-sm me-2"
                                  role="status"
                                  aria-hidden="true"
                                ></span>
                                Uploading Image...
                              </>
                            ) : isAddingBlog || isUpdatingBlog ? (
                              <>
                                <span
                                  className="spinner-border spinner-border-sm me-2"
                                  role="status"
                                  aria-hidden="true"
                                ></span>
                                {editingBlog ? "Updating..." : "Adding..."}
                              </>
                            ) : editingBlog ? (
                              "Update Blog"
                            ) : (
                              "Add Blog"
                            )}
                          </button>
                        </div>
                      </div>
                    </form>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        <div className="d-flex justify-content-end mb-3">
          <select
            className="form-select w-auto bg-transparent"
            value={analyticsWindow}
            onChange={(event) => setAnalyticsWindow(event.target.value)}
          >
            <option className="text-dark" value="1d">
              Yesterday
            </option>
            <option className="text-dark" value="7d">
              Last 7 days
            </option>
            <option className="text-dark" value="30d">
              Last 30 days
            </option>
          </select>
        </div>
        <div className="row g-4 mb-5">
          <div className="col-md-4">
            <div className="glass-card h-100 p-4">
              <div className="d-flex justify-content-between align-items-center mb-3">
                <div className="icon-box bg-primary bg-opacity-10 p-3 rounded-4">
                  <svg
                    width="24"
                    height="24"
                    viewBox="0 0 24 24"
                    fill="none"
                    stroke="currentColor"
                    strokeWidth="2"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    className="text-primary"
                  >
                    <path d="M2 12s3-7 10-7 10 7 10 7-3 7-10 7-10-7-10-7Z" />
                    <circle cx="12" cy="12" r="3" />
                  </svg>
                </div>
                <span
                  className={`badge bg-${
                    stats.viewsChangePct > 0
                      ? "success"
                      : stats.viewsChangePct < 0
                        ? "danger"
                        : "secondary"
                  } bg-opacity-10 text-${
                    stats.viewsChangePct > 0
                      ? "success"
                      : stats.viewsChangePct < 0
                        ? "danger"
                        : "secondary"
                  } border-0 px-3 py-2 rounded-pill`}
                >
                  {stats.viewsChangePct > 0 ? "+" : ""}
                  {stats.viewsChangePct}%
                </span>
              </div>
              <h5 className="text-muted fw-medium mb-1">Total Views</h5>
              <h2 className="display-6 fw-bold mb-0">
                {stats.views.toLocaleString()}
              </h2>
              <small className="text-muted">
                Avg time: {Math.round(stats.avgTimeSeconds)}s
              </small>
            </div>
          </div>
          <div className="col-md-4">
            <div className="glass-card h-100 p-4">
              <div className="d-flex justify-content-between align-items-center mb-3">
                <div
                  className="icon-box bg-purple bg-opacity-10 p-3 rounded-4"
                  style={{ backgroundColor: "rgba(124, 58, 237, 0.1)" }}
                >
                  <svg
                    width="24"
                    height="24"
                    viewBox="0 0 24 24"
                    fill="none"
                    stroke="#7c3aed"
                    strokeWidth="2"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                  >
                    <path d="m12 3 8 4.5v9L12 21l-8-4.5v-9L12 3Z" />
                    <path d="m12 12 8-4.5" />
                    <path d="M12 12v9" />
                    <path d="m12 12-8-4.5" />
                    <path d="m16 5.25-8 4.5" />
                  </svg>
                </div>
                <span
                  className={`badge bg-${
                    stats.projectsChangePct > 0
                      ? "success"
                      : stats.projectsChangePct < 0
                        ? "danger"
                        : "secondary"
                  } bg-opacity-10 text-${
                    stats.projectsChangePct > 0
                      ? "success"
                      : stats.projectsChangePct < 0
                        ? "danger"
                        : "secondary"
                  } border-0 px-3 py-2 rounded-pill`}
                >
                  {stats.projectsChangePct > 0 ? "+" : ""}
                  {stats.projectsChangePct}%
                </span>
              </div>
              <h5 className="text-muted fw-medium mb-1">Projects</h5>
              <h2 className="display-6 fw-bold mb-0">{stats.projects}</h2>
            </div>
          </div>
          <div className="col-md-4">
            <div className="glass-card h-100 p-4">
              <div className="d-flex justify-content-between align-items-center mb-3">
                <div className="icon-box bg-info bg-opacity-10 p-3 rounded-4">
                  <svg
                    width="24"
                    height="24"
                    viewBox="0 0 24 24"
                    fill="none"
                    stroke="currentColor"
                    strokeWidth="2"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    className="text-info"
                  >
                    <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z" />
                  </svg>
                </div>
                <span className="badge bg-success bg-opacity-10 text-success border-0 px-3 py-2 rounded-pill">
                  +8%
                </span>
              </div>
              <h5 className="text-muted fw-medium mb-1">Messages</h5>
              <h2 className="display-6 fw-bold mb-0">{stats.messages}</h2>
            </div>
          </div>
        </div>

        <div className="mt-5">
          <div className="glass-card p-4">
            <h3 className="mb-4">Recent Activity</h3>

            {tab === "projects" && (
              <>
                {/* Desktop Table View */}
                <div className="table-responsive d-none d-md-block">
                  <table className="table table-hover table-transparent mb-0">
                    <thead>
                      <tr>
                        <th>Project</th>
                        <th>Subheading</th>
                        <th>Status</th>
                        <th>Date</th>
                        <th>Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {isProjectsLoading ? (
                        <tr>
                          <td colSpan="5" className="text-center py-3">
                            Loading projects...
                          </td>
                        </tr>
                      ) : projects.length === 0 ? (
                        <tr>
                          <td colSpan="5" className="text-center py-3">
                            No projects found
                          </td>
                        </tr>
                      ) : (
                        projects.map((project) => (
                          <tr key={project._id} id={`project-${project._id}`}>
                            <td>{project.title}</td>
                            <td>
                              <span className="text-muted small">
                                {project.subheading || "-"}
                              </span>
                            </td>
                            <td>
                              <span
                                className={`badge bg-${project.status === "Completed" ? "success" : project.status === "In Progress" ? "warning" : "primary"}`}
                              >
                                {project.status}
                              </span>
                            </td>
                            <td>
                              {new Date(project.createdAt).toLocaleDateString(
                                "en-US",
                                {
                                  year: "numeric",
                                  month: "short",
                                  day: "numeric",
                                },
                              )}
                            </td>
                            <td>
                              <button
                                onClick={() => handleEditClick(project)}
                                className="btn btn-outline-info btn-sm py-0 px-2 me-2"
                              >
                                Edit
                              </button>
                              <button
                                onClick={() => handleDeleteProject(project._id)}
                                className="btn btn-outline-danger btn-sm py-0 px-2"
                              >
                                &times;
                              </button>
                            </td>
                          </tr>
                        ))
                      )}
                    </tbody>
                  </table>
                </div>

                {/* Mobile Card View */}
                <div className="d-md-none">
                  {isProjectsLoading ? (
                    <div className="text-center py-3">Loading projects...</div>
                  ) : projects.length === 0 ? (
                    <div className="text-center py-3">No projects found</div>
                  ) : (
                    <div className="d-flex flex-column gap-3">
                      {projects.map((project) => (
                        <div
                          key={project._id}
                          className={`glass-card p-3 border-start border-4 ${
                            project.status === "Completed"
                              ? "border-status-completed"
                              : project.status === "In Progress"
                                ? "border-status-progress"
                                : "border-status-review"
                          }`}
                        >
                          <div className="d-flex justify-content-between align-items-start mb-2">
                            <div>
                              <h6 className="fw-bold mb-0">{project.title}</h6>
                              <p className="text-muted small mb-0">
                                {project.subheading}
                              </p>
                            </div>
                            <span
                              className={`badge bg-${project.status === "Completed" ? "success" : project.status === "In Progress" ? "warning" : "primary"} border-0`}
                            >
                              {project.status}
                            </span>
                          </div>
                          <div className="d-flex justify-content-between align-items-center">
                            <small className="text-muted">
                              {new Date(project.createdAt).toLocaleDateString(
                                "en-US",
                                {
                                  year: "numeric",
                                  month: "short",
                                  day: "numeric",
                                },
                              )}
                            </small>
                            <div className="d-flex gap-2">
                              <button
                                onClick={() => handleEditClick(project)}
                                className="btn btn-premium btn-sm py-1 px-3"
                              >
                                Edit
                              </button>
                              <button
                                onClick={() => handleDeleteProject(project._id)}
                                className="btn btn-outline-danger btn-sm py-1"
                              >
                                Delete
                              </button>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </>
            )}

            {tab === "blogs" && (
              <>
                {/* Desktop Table View */}
                <div className="table-responsive d-none d-md-block">
                  <table className="table table-hover table-transparent mb-0">
                    <thead>
                      <tr>
                        <th>Blog</th>
                        <th>Subheading</th>
                        <th>Status</th>
                        <th>Date</th>
                        <th>Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {isBlogsLoading ? (
                        <tr>
                          <td colSpan="5" className="text-center py-3">
                            Loading blogs...
                          </td>
                        </tr>
                      ) : blogs.length === 0 ? (
                        <tr>
                          <td colSpan="5" className="text-center py-3">
                            No blogs found
                          </td>
                        </tr>
                      ) : (
                        blogs.map((blog) => (
                          <tr key={blog._id} id={`blog-${blog._id}`}>
                            <td>{blog.title}</td>
                            <td>
                              <span className="text-muted small">
                                {blog.subheading || "-"}
                              </span>
                            </td>
                            <td>
                              <span
                                className={`badge bg-${blog.status === "Published" ? "success" : "warning"}`}
                              >
                                {blog.status}
                              </span>
                            </td>
                            <td>
                              {new Date(blog.createdAt).toLocaleDateString(
                                "en-US",
                                {
                                  year: "numeric",
                                  month: "short",
                                  day: "numeric",
                                },
                              )}
                            </td>
                            <td>
                              <button
                                onClick={() => handleEditBlogClick(blog)}
                                className="btn btn-outline-info btn-sm py-0 px-2 me-2"
                              >
                                Edit
                              </button>
                              <button
                                onClick={() => handleDeleteBlog(blog._id)}
                                className="btn btn-outline-danger btn-sm py-0 px-2"
                              >
                                &times;
                              </button>
                            </td>
                          </tr>
                        ))
                      )}
                    </tbody>
                  </table>
                </div>

                {/* Mobile Card View */}
                <div className="d-md-none">
                  {isBlogsLoading ? (
                    <div className="text-center py-3">Loading blogs...</div>
                  ) : blogs.length === 0 ? (
                    <div className="text-center py-3">No blogs found</div>
                  ) : (
                    <div className="d-flex flex-column gap-3">
                      {blogs.map((blog) => (
                        <div
                          key={blog._id}
                          className={`glass-card p-3 border-start border-4 ${
                            blog.status === "Published"
                              ? "border-status-completed"
                              : "border-status-review"
                          }`}
                        >
                          <div className="d-flex justify-content-between align-items-start mb-2">
                            <div>
                              <h6 className="fw-bold mb-0">{blog.title}</h6>
                              <p className="text-muted small mb-0">
                                {blog.subheading}
                              </p>
                            </div>
                            <span
                              className={`badge bg-${blog.status === "Published" ? "success" : "warning"} border-0`}
                            >
                              {blog.status}
                            </span>
                          </div>
                          <div className="d-flex justify-content-between align-items-center">
                            <small className="text-muted">
                              {new Date(blog.createdAt).toLocaleDateString(
                                "en-US",
                                {
                                  year: "numeric",
                                  month: "short",
                                  day: "numeric",
                                },
                              )}
                            </small>
                            <div className="d-flex gap-2">
                              <button
                                onClick={() => handleEditBlogClick(blog)}
                                className="btn btn-premium btn-sm py-1 px-3"
                              >
                                Edit
                              </button>
                              <button
                                onClick={() => handleDeleteBlog(blog._id)}
                                className="btn btn-outline-danger btn-sm py-1"
                              >
                                Delete
                              </button>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </>
            )}
          </div>
        </div>

        {user?.role === "admin" && (
          <div className="mt-5">
            <div className="glass-card p-4">
              <h3 className="mb-4">Viewer Details</h3>
              <div className="table-responsive">
                <table className="table table-hover table-transparent mb-0">
                  <thead>
                    <tr>
                      <th>IP Hash</th>
                      <th>Last Path</th>
                      <th>Total Time</th>
                      <th>Last Seen</th>
                      <th>First Seen</th>
                    </tr>
                  </thead>
                  <tbody>
                    {isSessionsLoading ? (
                      <tr>
                        <td colSpan="5" className="text-center py-3">
                          Loading viewers...
                        </td>
                      </tr>
                    ) : analyticsSessionsData?.data?.length ? (
                      analyticsSessionsData.data.map((session) => (
                        <tr key={session._id}>
                          <td className="text-muted small">
                            {session.ipHash?.slice(0, 12)}...
                          </td>
                          <td>
                            <span className="text-muted small">
                              {session.lastPath || "-"}
                            </span>
                          </td>
                          <td>{Math.round(session.totalTimeSeconds || 0)}s</td>
                          <td>
                            {new Date(session.lastSeenAt).toLocaleString(
                              "en-US",
                            )}
                          </td>
                          <td>
                            {new Date(session.firstSeenAt).toLocaleString(
                              "en-US",
                            )}
                          </td>
                        </tr>
                      ))
                    ) : (
                      <tr>
                        <td colSpan="5" className="text-center py-3">
                          No viewers yet
                        </td>
                      </tr>
                    )}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        )}
      </div>
    </ProtectedRoute>
  );
}

export default function DashboardPage() {
  return (
    <Suspense
      fallback={
        <div className="container py-5 text-center">Loading dashboard...</div>
      }
    >
      <DashboardContent />
    </Suspense>
  );
}

"use client";

import { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useSubscribeMutation } from "@/store/services/blogsApi";
import { IoClose, IoMailOutline, IoPersonOutline } from "react-icons/io5";

export default function SubscribeModal({ isOpen, onClose, onSuccess }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [subscribe, { isLoading, error }] = useSubscribeMutation();
  const [isSuccess, setIsSuccess] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const result = await subscribe({ name, email }).unwrap();
      if (result.success) {
        localStorage.setItem("blogSubscriberName", name);
        localStorage.setItem("blogSubscriberEmail", email);
        setIsSuccess(true);
        setTimeout(() => {
          onSuccess(email);
          onClose();
        }, 2000);
      }
    } catch (err) {
      console.error("Subscription failed:", err);
    }
  };

  return (
    <AnimatePresence>
      {isOpen && (
        <div className="modal-overlay">
          <motion.div
            initial={{ opacity: 0, scale: 0.9, y: 20 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.9, y: 20 }}
            className="subscribe-modal-content glass-card p-4 p-md-5"
          >
            <button className="close-btn" onClick={onClose}>
              <IoClose size={24} />
            </button>

            {isSuccess ? (
              <div className="text-center py-4">
                <motion.div
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  className="success-icon mb-3"
                >
                  🎉
                </motion.div>
                <h3>Thank You!</h3>
                <p className="text-muted">
                  You're now subscribed. Unlocking your interaction...
                </p>
              </div>
            ) : (
              <>
                <div className="text-center mb-4">
                  <h2 className="fw-bold">Join the Community</h2>
                  <p className="text-muted small">
                    Subscribe to like, reply, and share our latest stories.
                  </p>
                </div>

                <form onSubmit={handleSubmit}>
                  <div className="mb-3 input-group-modern">
                    <IoPersonOutline className="input-icon" />
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Your Name"
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                      required
                    />
                  </div>
                  <div className="mb-4 input-group-modern">
                    <IoMailOutline className="input-icon" />
                    <input
                      type="email"
                      className="form-control"
                      placeholder="Email Address"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      required
                    />
                  </div>

                  {error && (
                    <p className="text-danger small mb-3">
                      {error.data?.message || "Something went wrong"}
                    </p>
                  )}

                  <button
                    type="submit"
                    className="btn btn-primary w-100 py-2 fw-bold"
                    disabled={isLoading}
                  >
                    {isLoading ? "Subscribing..." : "Subscribe Now"}
                  </button>
                </form>
              </>
            )}
          </motion.div>

          <style jsx>{`
            .modal-overlay {
              position: fixed;
              top: 0;
              left: 0;
              right: 0;
              bottom: 0;
              background: rgba(0, 0, 0, 0.4);
              backdrop-filter: blur(5px);
              display: flex;
              align-items: center;
              justify-content: center;
              z-index: 1050;
              padding: 20px;
            }
            .subscribe-modal-content {
              position: relative;
              max-width: 450px;
              width: 100%;
              background: rgba(255, 255, 255, 0.8) !important;
              box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
            }
            .close-btn {
              position: absolute;
              top: 15px;
              right: 15px;
              background: none;
              border: none;
              color: #6c757d;
              cursor: pointer;
              transition: color 0.2s;
            }
            .close-btn:hover {
              color: #343a40;
            }
            .input-group-modern {
              position: relative;
            }
            .input-icon {
              position: absolute;
              left: 12px;
              top: 50%;
              transform: translateY(-50%);
              color: #6c757d;
              z-index: 5;
            }
            .form-control {
              padding-left: 40px;
              border-radius: 10px;
              height: 48px;
              border: 1px solid rgba(0, 0, 0, 0.1);
              background: rgba(255, 255, 255, 0.5);
            }
            .form-control:focus {
              box-shadow: 0 0 0 3px rgba(13, 110, 253, 0.1);
              border-color: #0d6efd;
              background: #fff;
            }
            .success-icon {
              font-size: 3rem;
            }
          `}</style>
        </div>
      )}
    </AnimatePresence>
  );
}
